// src/pages/CartPage.js
import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';

export default function CartPage() {
  const { cartItems, removeFromCart, clearCart } = useCart();
  const [orderMessage, setOrderMessage] = useState('');
  const navigate = useNavigate();

  const handlePlaceOrder = async () => {
    if (!cartItems || cartItems.length === 0) {
      setOrderMessage('Cart is empty or not loaded properly.');
      return;
    }

    const items = cartItems.map((item) => ({
      artwork: item._id,
    }));

    try {
      await axios.post(
        'http://localhost:5000/api/orders/place',
        { items },
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        }
      );
      setOrderMessage('‚úÖ Order placed successfully!');
      clearCart();
      setTimeout(() => navigate('/my-orders'), 1500);
    } catch (err) {
      setOrderMessage(`‚ùå Order failed: ${err.response?.data?.message || 'Server error'}`);
      console.error('Order failed:', err);
    }
  };

  return (
    <div className="min-h-screen p-6 bg-black text-white fade-in">
      <div className="max-w-4xl mx-auto">
        <h1 className="glow-title text-4xl font-extrabold text-center mb-10">üõí Your Art Cart</h1>

        {orderMessage && (
          <p className="text-center mb-6 text-yellow-400 font-medium">{orderMessage}</p>
        )}

        {cartItems.length === 0 ? (
          <div className="text-center mt-20">
            <p className="text-xl text-gray-400">üñºÔ∏è Your cart is empty.</p>
            <p className="text-sm text-gray-500 mt-2">Add some beautiful art to your collection!</p>
          </div>
        ) : (
          <div className="space-y-6">
            {cartItems.map((item) => (
              <div
                key={item._id}
                className="cart-card flex items-center justify-between"
              >
                <div>
                  <h2 className="text-lg font-semibold text-cyan-200">{item.title}</h2>
                  <p className="text-sm text-gray-300">üí∞ ‚Çπ{item.price}</p>
                </div>
                <button
                  onClick={() => removeFromCart(item._id)}
                  className="text-red-400 hover:text-red-600 transition-all duration-200"
                >
                  ‚ùå Remove
                </button>
              </div>
            ))}

            <div className="text-center mt-8">
              <button
                onClick={handlePlaceOrder}
                className="glow-button"
              >
                üßæ Place Order
              </button>
            </div>
          </div>
        )}
      </div>

      <style>{`
        .fade-in {
          animation: fadeIn 0.6s ease-in-out;
        }

        .glow-title {
          background: linear-gradient(to right, #00ffff, #00bfff, #0077ff);
          -webkit-background-clip: text;
          -webkit-text-fill-color: transparent;
          text-shadow: 0 0 10px #00ffff88;
        }

        .cart-card {
          background: rgba(255, 255, 255, 0.05);
          border: 1px solid rgba(0, 255, 255, 0.2);
          backdrop-filter: blur(14px);
          border-radius: 16px;
          padding: 1rem 1.5rem;
          box-shadow: 0 0 20px rgba(0, 255, 255, 0.05);
          transition: transform 0.3s ease, box-shadow 0.3s ease;
        }

        .cart-card:hover {
          transform: scale(1.01);
          box-shadow: 0 0 25px rgba(0, 255, 255, 0.2);
        }

        .glow-button {
          background: linear-gradient(to right, #00ffff, #0077ff);
          padding: 12px 30px;
          border: none;
          border-radius: 50px;
          font-size: 1.1rem;
          font-weight: bold;
          color: white;
          cursor: pointer;
          box-shadow: 0 0 10px #00ffff88;
          transition: all 0.3s ease;
        }

        .glow-button:hover {
          transform: scale(1.05);
          box-shadow: 0 0 20px #00ffffcc;
        }

        @keyframes fadeIn {
          0% { opacity: 0; transform: scale(0.95); }
          100% { opacity: 1; transform: scale(1); }
        }
      `}</style>
    </div>
  );
}
