import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function MyOrders() {
  const [orders, setOrders] = useState([]);
  const [isLoggedIn, setIsLoggedIn] = useState(true);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      setIsLoggedIn(false);
      setLoading(false);
      return;
    }

    const fetchOrders = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/orders/my-orders', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setOrders(res.data);
      } catch (err) {
        console.error('Failed to fetch orders:', err);
        setIsLoggedIn(false);
      } finally {
        setLoading(false);
      }
    };

    fetchOrders();
  }, []);

  if (loading) return <div className="text-center p-8 text-blue-500 text-lg animate-pulse">Loading orders...</div>;

  if (!isLoggedIn) {
    return (
      <div className="p-8 text-center">
        <h2 className="text-2xl font-bold text-red-600 drop-shadow-glow">Please login to view your orders.</h2>
      </div>
    );
  }

  if (orders.length === 0) {
    return (
      <div className="p-8 text-center">
        <h2 className="text-2xl font-bold drop-shadow-glow">No orders yet.</h2>
      </div>
    );
  }

  return (
    <div className="p-8 max-w-5xl mx-auto">
      <h2 className="text-3xl font-extrabold mb-6 text-center drop-shadow-glow text-cyan-400">My Orders</h2>

      {orders.map((order) => (
        <div key={order._id} className="mb-8 border rounded-2xl p-5 frosted-bg shadow-lg card-hover-effect">
          <div className="flex justify-between items-center mb-4">
            <p className="text-gray-300 text-sm">
              Placed on:{' '}
              {new Date(order.placedAt || order.createdAt).toLocaleDateString('en-IN', {
                year: 'numeric',
                month: 'short',
                day: 'numeric',
              })}
            </p>
            <button
              onClick={() => handleDelete(order._id)}
              className="bg-red-500 text-white px-4 py-1.5 rounded-xl delete-btn font-semibold"
            >
              Delete Order
            </button>
          </div>

          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4">
            {order.items.map((item, index) => (
              <div
                key={index}
                className="border border-gray-700 p-3 rounded-xl shadow-md bg-black/20 card-hover-effect"
              >
                <img
                  src={`http://localhost:5000${item.imageUrl}`}
                  alt={item.title}
                  className="w-full h-48 object-cover rounded-lg mb-2 image-fade-hover"
                />
                <h3 className="text-lg font-semibold text-white">{item.title}</h3>
                <p className="text-cyan-300 font-medium">â‚¹{Number(item.price).toFixed(2)}</p>
              </div>
            ))}
          </div>
        </div>
      ))}

      <style>{`
        .drop-shadow-glow {
          text-shadow: 0 0 6px #00fff7, 0 0 12px #00fff7, 0 0 20px #00fff7;
        }

        .card-hover-effect {
          transition: transform 0.3s ease, box-shadow 0.3s ease;
        }

        .card-hover-effect:hover {
          transform: scale(1.02);
          box-shadow: 0 0 10px #00fff7, 0 0 15px rgba(0, 255, 255, 0.3);
        }

        .frosted-bg {
          backdrop-filter: blur(10px);
          background-color: rgba(255, 255, 255, 0.05);
          border: 1px solid rgba(255, 255, 255, 0.1);
        }

        .delete-btn {
          transition: all 0.3s ease;
          box-shadow: 0 0 5px rgba(255, 0, 0, 0.5);
        }

        .delete-btn:hover {
          background-color: #ff4d4d;
          box-shadow: 0 0 12px #ff4d4d, 0 0 18px rgba(255, 77, 77, 0.4);
          transform: scale(1.05);
        }

        .image-fade-hover {
          transition: all 0.3s ease;
        }

        .image-fade-hover:hover {
          filter: brightness(1.15);
          transform: scale(1.05);
        }
      `}</style>
    </div>
  );

  async function handleDelete(orderId) {
    const token = localStorage.getItem('token');
    if (!token) return;

    if (!window.confirm('Are you sure you want to delete this order?')) return;

    try {
      await axios.delete(`http://localhost:5000/api/orders/${orderId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      setOrders((prev) => prev.filter((o) => o._id !== orderId));
    } catch (err) {
      console.error('Failed to delete order:', err);
      alert('Could not delete the order.');
    }
  }
}
