import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';

export default function Home() {
  return (
    <>
      <div className="max-w-6xl mx-auto px-4 py-8">
        <h1 className="text-3xl font-bold mb-6 text-center text-blue-900">
          Welcome to Yuvi's Artistry
        </h1>

        <Carousel
          autoPlay
          infiniteLoop
          showThumbs={false}
          showStatus={false}
          className="mb-10"
        >
          {/* Custom static slides */}
          <div>
            <img src="/images/art1.jpg" alt="Art 1" className="h-[500px] w-full object-cover rounded-xl shadow-lg" />
            <p className="legend text-lg p-2">Art Title 1 - ₹999</p>
          </div>
          <div>
            <img src="/images/art2.jpg" alt="Art 2" className="h-[500px] w-full object-cover rounded-xl shadow-lg" />
            <p className="legend text-lg p-2">Art Title 2 - ₹1499</p>
          </div>
          <div>
            <img src="/images/art3.jpg" alt="Art 3" className="h-[500px] w-full object-cover rounded-xl shadow-lg" />
            <p className="legend text-lg p-2">Art Title 3 - ₹1999</p>
          </div>
          <div>
            <img src="/images/art4.jpeg" alt="Art 4" className="h-[500px] w-full object-cover rounded-xl shadow-lg" />
            <p className="legend text-lg p-2">Art Title 4 - ₹2499</p>
          </div>
          <div>
            <img src="/images/art5.jpeg" alt="Art 5" className="h-[500px] w-full object-cover rounded-xl shadow-lg" />
            <p className="legend text-lg p-2">Art Title 5 - ₹2999</p>
          </div>
        </Carousel>
      </div>

      <style>{`
        .carousel .slide {
          background-color: #f3f4f6;
          width: 100%;
          height: 76vh;
          display: flex;
          align-items: center;
          justify-content: center;
          position: relative;
        }
        
        .carousel .legend {
          font-size: 1.25rem;
          font-weight: bold;
          text-align: center;
          background: linear-gradient(45deg, rgba(0,0,0,0.7), rgba(0,0,0,0.5));
          color: white;
          padding: 10px 20px;
          border-radius: 10px;
          position: absolute;
          bottom: 20px;
          left: 50%;
          transform: translateX(-50%);
          box-shadow: 0 4px 10px rgba(0,0,0,0.3);
          transition: transform 0.3s ease, opacity 0.3s ease;
        }
        
        .carousel .legend:hover {
          transform: translateX(-50%) scale(1.05);
          opacity: 0.9;
        }
        
        .carousel .thumb {
          border-radius: 8px;
        }
      `}</style>
    </>
  );
}
