import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Checkout = () => {
  const [cart, setCart] = useState([]);

  useEffect(() => {
    const storedCart = JSON.parse(localStorage.getItem('cart')) || [];
    setCart(storedCart);
  }, []);

  const placeOrder = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        alert('Please login to place your order');
        return;
      }

      const response = await axios.post(
        'http://localhost:5000/api/orders', // ✅ Make sure backend is running
        { items: cart },
        {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
        }
      );

      alert(response.data.message || 'Order placed successfully!');
      localStorage.removeItem('cart');
      setCart([]);
      window.location.href = '/'; // redirect after placing order
    } catch (err) {
      console.error('Order error:', err);
      alert(
        err.response?.data?.message || 'Error placing order. Please try again.'
      );
    }
  };

  const total = cart.reduce((sum, item) => sum + item.price, 0);

  return (
    <div className="p-6 max-w-2xl mx-auto">
      <h2 className="text-3xl font-bold mb-6">Checkout</h2>

      {cart.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <>
          <ul className="space-y-4">
            {cart.map((item, i) => (
              <li key={i} className="border p-4 rounded shadow-sm">
                <h3 className="text-xl">{item.title}</h3>
                <p className="text-gray-700">₹{item.price}</p>
              </li>
            ))}
          </ul>
          <div className="mt-6 text-2xl font-semibold">Total: ₹{total}</div>
          <button
            onClick={placeOrder}
            className="mt-6 px-6 py-3 bg-green-600 text-white rounded-lg hover:bg-green-700 transition"
          >
            Place Order
          </button>
        </>
      )}
    </div>
  );
};

export default Checkout;
