import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function Profile() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const fetchProfile = async () => {
      const token = localStorage.getItem('token');
      if (!token) return;

      try {
        const res = await axios.get('http://localhost:5000/api/auth/profile', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        setUser(res.data.user);
      } catch (err) {
        console.error('Failed to load profile:', err);
      }
    };

    fetchProfile();
  }, []);

  if (!user) {
    return <div className="p-6 text-center text-white">Loading profile...</div>;
  }

  const handleLogout = () => {
    localStorage.removeItem('token');
    window.location.href = '/admin';
  };

  return (
    <div className="profile-wrapper">
      <style>{profileStyles}</style>
      <div className="profile-card">
        <h2 className="profile-title">ðŸ‘¤ Profile</h2>
        <p><strong>Name:</strong> {user.name}</p>
        <p><strong>Email:</strong> {user.email}</p>
        <p><strong>Artworks Uploaded:</strong> {user.uploadedArtworks?.length || 0}</p>
        <button onClick={handleLogout} className="logout-btn">ðŸšª Logout</button>
      </div>
    </div>
  );
}

const profileStyles = `
body {
  background-color: #000;
}

.profile-wrapper {
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 3rem 1rem;
  min-height: 100vh;
  background: black;
  font-family: 'Segoe UI', sans-serif;
}

.profile-card {
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(20px);
  border: 1px solid rgba(255,255,255,0.1);
  padding: 2rem;
  border-radius: 20px;
  box-shadow: 0 10px 30px rgba(0,255,255,0.1);
  color: #fff;
  max-width: 400px;
  width: 100%;
  text-align: left;
  transition: transform 0.3s ease;
}

.profile-card:hover {
  transform: translateY(-5px);
  box-shadow: 0 15px 40px rgba(0,255,255,0.2);
}

.profile-title {
  font-size: 1.8rem;
  font-weight: bold;
  margin-bottom: 1.5rem;
  color: #00fff7;
  text-shadow: 0 0 5px #00fff7, 0 0 10px #00fff7;
}

.profile-card p {
  margin-bottom: 0.75rem;
  font-size: 1rem;
  line-height: 1.4;
}

.logout-btn {
  margin-top: 1.5rem;
  background: #ff6b6b;
  border: none;
  padding: 10px 20px;
  color: #fff;
  font-weight: bold;
  border-radius: 30px;
  cursor: pointer;
  transition: background 0.3s;
}

.logout-btn:hover {
  background: #ff4757;
}
`;
