import { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

export default function Upload() {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [price, setPrice] = useState('');
  const [imageFile, setImageFile] = useState(null);
  const [preview, setPreview] = useState('');
  const [loading, setLoading] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(true);

  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      setIsLoggedIn(false);
    }
  }, []);

  const handleImageUpload = async () => {
    const formData = new FormData();
    formData.append('image', imageFile);

    const res = await axios.post('http://localhost:5000/api/upload/upload', formData);
    return res.data.imageUrl;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (!token) {
      alert('You must be logged in.');
      return;
    }

    try {
      setLoading(true);
      const imageUrl = await handleImageUpload();

      await axios.post(
        'http://localhost:5000/api/artworks',
        { title, description, price, imageUrl },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      alert('Artwork uploaded successfully!');
      navigate('/artgallery');
    } catch (err) {
      console.error(err);
      alert('Upload failed!');
    } finally {
      setLoading(false);
    }
  };

  if (!isLoggedIn) {
    return (
      <div className="text-center mt-10 p-6 text-red-600 font-medium text-lg bg-red-50 border border-red-200 rounded-xl shadow-sm">
        Please login to upload your artwork.
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-pink-50 via-white to-indigo-50 flex items-center justify-center px-4 py-8">
      <div className="w-full max-w-xl bg-white/50 backdrop-blur-md rounded-3xl p-10 border border-gray-200 shadow-2xl transition-transform hover:scale-[1.01]">
        <h2 className="text-4xl font-serif font-semibold text-center text-indigo-800 mb-8 tracking-wide">
          Upload Your Artwork
        </h2>
        <form onSubmit={handleSubmit} className="space-y-6 text-gray-800">
          <div>
            <label className="block mb-2 text-sm font-semibold">Artwork Title</label>
            <input
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              placeholder="e.g., Evening Serenity"
              required
              className="w-full px-4 py-3 rounded-lg border border-gray-300 bg-white/80 shadow-sm focus:ring-2 focus:ring-indigo-300 focus:outline-none transition"
            />
          </div>
          <div>
            <label className="block mb-2 text-sm font-semibold">Description</label>
            <textarea
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              placeholder="Describe your art in a few words"
              required
              rows="4"
              className="w-full px-4 py-3 rounded-lg border border-gray-300 bg-white/80 shadow-sm focus:ring-2 focus:ring-indigo-300 focus:outline-none transition"
            />
          </div>
          <div>
            <label className="block mb-2 text-sm font-semibold">Price (â‚¹)</label>
            <input
              type="number"
              value={price}
              onChange={(e) => setPrice(e.target.value)}
              placeholder="Enter price"
              required
              className="w-full px-4 py-3 rounded-lg border border-gray-300 bg-white/80 shadow-sm focus:ring-2 focus:ring-indigo-300 focus:outline-none transition"
            />
          </div>
          <div>
            <label className="block mb-2 text-sm font-semibold">Artwork Image</label>
            <input
              type="file"
              accept="image/*"
              onChange={(e) => {
                setImageFile(e.target.files[0]);
                setPreview(URL.createObjectURL(e.target.files[0]));
              }}
              required
              className="w-full text-sm text-gray-700 file:mr-4 file:py-2 file:px-4 file:rounded-full
                         file:border-0 file:text-sm file:font-semibold
                         file:bg-indigo-600 file:text-white hover:file:bg-indigo-700 transition"
            />
          </div>
          {preview && (
            <img
              src={preview}
              alt="Preview"
              className="mt-4 rounded-xl border border-indigo-200 shadow-lg max-h-64 object-contain mx-auto"
            />
          )}
          <button
            type="submit"
            disabled={loading}
            className="w-full py-3 text-lg font-semibold text-white bg-indigo-600 rounded-xl shadow-md hover:bg-indigo-700 transition-all duration-200"
          >
            {loading ? 'Uploading...' : 'Upload Artwork'}
          </button>
        </form>
      </div>
    </div>
  );
}
