import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';

export default function Gallery() {
  const [artworks, setArtworks] = useState([]);
  const [search, setSearch] = useState('');
  const [minPrice, setMinPrice] = useState('');
  const [maxPrice, setMaxPrice] = useState('');
  const navigate = useNavigate();

  const { addToCart } = useCart();

  const fetchArtworks = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/artworks', {
        params: { search, minPrice, maxPrice },
      });
      setArtworks(res.data);
    } catch (err) {
      console.error('Error loading artworks:', err);
    }
  };

  useEffect(() => {
    fetchArtworks();
  }, []);

  const handleFilter = (e) => {
    e.preventDefault();
    fetchArtworks();
  };

  const handleBuyNow = (art) => {
    addToCart(art);
    navigate('/cart');
  };

  return (
    <div className="gallery-container">
      <h1 className="gallery-header">üé® Art with Soul</h1>

      <form onSubmit={handleFilter} className="filter-form">
        <input
          type="text"
          placeholder="Search"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          className="filter-input"
        />
        <input
          type="number"
          placeholder="Min ‚Çπ"
          value={minPrice}
          onChange={(e) => setMinPrice(e.target.value)}
          className="filter-input"
        />
        <input
          type="number"
          placeholder="Max ‚Çπ"
          value={maxPrice}
          onChange={(e) => setMaxPrice(e.target.value)}
          className="filter-input"
        />
        <button type="submit" className="filter-button">üîç Go</button>
      </form>

      <div className="artwork-grid">
        {artworks.map((art) => (
          <div key={art._id} className="artwork-card">
            <div className="image-wrapper">
              <img
                src={`http://localhost:5000${art.imageUrl}`}
                alt={art.title}
                className="artwork-image"
              />
            </div>

            <Link to={`/art/${art._id}`}>
              <h2 className="artwork-title">{art.title}</h2>
            </Link>

            <div className="artwork-meta">
              <span className="price-tag">‚Çπ{art.price}</span>
            </div>

            <p className="artwork-description">{art.description}</p>

            <button onClick={() => handleBuyNow(art)} className="buy-button">
              üõçÔ∏è Buy Now
              <span className="ripple"></span>
            </button>

            <Link to={`/edit/${art._id}`} className="edit-button">üõ†Ô∏è</Link>
          </div>
        ))}
      </div>

      <style>{ExtraStyles}</style>
    </div>
  );
}

