import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function MyOrders() {
  const [orders, setOrders] = useState([]);
  const [isLoggedIn, setIsLoggedIn] = useState(true);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      setIsLoggedIn(false);
      setLoading(false);
      return;
    }

    const fetchOrders = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/orders/my-orders', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setOrders(res.data);
      } catch (err) {
        console.error('Failed to fetch orders:', err);
        setIsLoggedIn(false);
      } finally {
        setLoading(false);
      }
    };

    fetchOrders();
  }, []);

  if (loading) return <div className="text-center p-8 text-cyan-400 text-xl animate-pulse">üåü Loading your masterpieces...</div>;

  if (!isLoggedIn) {
    return (
      <div className="p-8 text-center">
        <h2 className="text-2xl font-bold text-red-600 animate-bounce">üö´ Please login to view your orders.</h2>
      </div>
    );
  }

  if (orders.length === 0) {
    return (
      <div className="p-8 text-center">
        <h2 className="text-2xl font-bold text-gray-300">üñºÔ∏è No orders yet. Start collecting art!</h2>
      </div>
    );
  }

  return (
    <div className="p-8 max-w-6xl mx-auto text-white">
      <h2 className="text-4xl font-extrabold text-cyan-400 mb-8 drop-shadow-glow text-center animate-fadein">üßæ My Orders</h2>

      {orders.map((order) => (
        <div
          key={order._id}
          className="mb-10 border border-cyan-600 rounded-2xl bg-gradient-to-br from-[#0f172a] to-[#020617] shadow-2xl shadow-cyan-700/40 p-6 transition-transform duration-300 hover:scale-[1.01] backdrop-blur-md bg-opacity-80 glow-border"
        >
          <div className="flex flex-col md:flex-row justify-between items-center mb-4 gap-3">
            <p className="text-sm text-gray-400">
              üóìÔ∏è Placed on:{' '}
              <span className="text-cyan-300">
                {new Date(order.placedAt || order.createdAt).toLocaleDateString('en-IN', {
                  year: 'numeric',
                  month: 'short',
                  day: 'numeric',
                })}
              </span>
            </p>
            <button
              onClick={() => handleDelete(order._id)}
              className="px-4 py-2 bg-red-600 text-white rounded-full shadow-lg hover:bg-red-700 hover:shadow-red-400/40 transition-all duration-300 delete-button"
            >
              ‚ùå Delete Order
            </button>
          </div>

          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6 custom-scrollbar overflow-y-auto max-h-[600px] pr-2">
            {order.items.map((item, index) => (
              <div
                key={index}
                className="border border-cyan-800 bg-gray-800/40 rounded-xl overflow-hidden shadow-inner p-4 hover:shadow-cyan-400/30 transition-all duration-300 hover:scale-105"
              >
                <img
                  src={`http://localhost:5000${item.imageUrl}`}
                  alt={item.title}
                  className="w-full h-48 object-cover rounded-md mb-3 transition-transform duration-300 hover:scale-110"
                />
                <h3 className="text-lg font-bold text-cyan-300 truncate">{item.title}</h3>
                <p className="text-md text-gray-300">üí∞ ‚Çπ{Number(item.price).toFixed(2)}</p>
              </div>
            ))}
          </div>
        </div>
      ))}

      <style>{`
        .drop-shadow-glow {
          text-shadow: 0 0 5px #00fff7, 0 0 10px #00fff7, 0 0 15px #00fff7;
        }

        .glow-border {
          box-shadow: 0 0 8px #0ff, 0 0 12px #0ff inset;
        }

        .delete-button {
          animation: pulseBtn 2s infinite;
        }

        @keyframes pulseBtn {
          0%, 100% {
            box-shadow: 0 0 5px rgba(255,0,0,0.4), 0 0 10px rgba(255,0,0,0.3);
          }
          50% {
            box-shadow: 0 0 15px rgba(255,0,0,0.7), 0 0 25px rgba(255,0,0,0.5);
          }
        }

        .animate-fadein {
          animation: fadeIn 1s ease-in;
        }

        @keyframes fadeIn {
          from { opacity: 0; transform: translateY(-10px); }
          to { opacity: 1; transform: translateY(0); }
        }

        .custom-scrollbar::-webkit-scrollbar {
          width: 8px;
        }

        .custom-scrollbar::-webkit-scrollbar-track {
          background: #0f172a;
        }

        .custom-scrollbar::-webkit-scrollbar-thumb {
          background-color: #00fff7;
          border-radius: 10px;
          border: 2px solid #0f172a;
        }
      `}</style>
    </div>
  );

  async function handleDelete(orderId) {
    const token = localStorage.getItem('token');
    if (!token) return;

    if (!window.confirm('Are you sure you want to delete this order?')) return;

    try {
      await axios.delete(`http://localhost:5000/api/orders/${orderId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      setOrders((prev) => prev.filter((o) => o._id !== orderId));
    } catch (err) {
      console.error('Failed to delete order:', err);
      alert('Could not delete the order.');
    }
  }
}
