import { useState } from 'react';
import axios from 'axios';

export default function Upload() {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [price, setPrice] = useState('');
  const [imageFile, setImageFile] = useState(null);
  const [preview, setPreview] = useState('');

  const handleImageUpload = async () => {
    const formData = new FormData();
    formData.append('image', imageFile);
    const res = await axios.post('http://localhost:5000/api/upload/upload', formData);
    return res.data.imageUrl;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    const imageUrl = await handleImageUpload();

    try {
      await axios.post('http://localhost:5000/api/artworks', {
        title, description, price, imageUrl
      }, {
        headers: { Authorization: token }
      });
      alert('Artwork uploaded!');
    } catch (err) {
      console.error('Artwork upload error:', err);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input value={title} onChange={(e) => setTitle(e.target.value)} placeholder="Title" required />
      <textarea value={description} onChange={(e) => setDescription(e.target.value)} placeholder="Description" required />
      <input value={price} type="number" onChange={(e) => setPrice(e.target.value)} placeholder="Price" required />
      <input type="file" onChange={(e) => {
        setImageFile(e.target.files[0]);
        setPreview(URL.createObjectURL(e.target.files[0]));
      }} required />
      {preview && <img src={preview} alt="Preview" width="200" />}
      <button type="submit">Upload</button>
    </form>
  );
}
