import { useState } from 'react';
import axios from 'axios';

export default function Upload() {
  const [title, setTitle] = useState('');
  const [imageFile, setImageFile] = useState(null);
  const [userId, setUserId] = useState('');
  const [price, setPrice] = useState('');
  const [preview, setPreview] = useState('');

  const handleImageUpload = async () => {
    if (!imageFile) {
      alert('Please select an image!');
      return null;
    }

    try {
      const formData = new FormData();
      formData.append('image', imageFile);

      const token = localStorage.getItem('token'); // Make sure user is logged in

      const res = await axios.post('http://localhost:5000/api/upload/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          Authorization: `Bearer ${token}`,
        },
      });

      return res.data.imageUrl;
    } catch (error) {
      console.error('Upload error:', error);
      alert('Image upload failed');
      return null;
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const imageUrl = await handleImageUpload();
    if (!imageUrl) return;

    try {
      const token = localStorage.getItem('token');

      await axios.post(
        'http://localhost:5000/api/artworks',
        {
          title,
          image: imageUrl,
          artistId: userId,
          price,
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      alert('Artwork posted!');
      // Optional: reset form
      setTitle('');
      setUserId('');
      setPrice('');
      setImageFile(null);
      setPreview('');
    } catch (error) {
      console.error('Post error:', error);
      alert('Failed to post artwork');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        value={userId}
        onChange={(e) => setUserId(e.target.value)}
        placeholder="User ID"
        required
      />
      <input
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        placeholder="Title"
        required
      />
      <input
        type="file"
        accept="image/*"
        onChange={(e) => {
          setImageFile(e.target.files[0]);
          setPreview(URL.createObjectURL(e.target.files[0]));
        }}
        required
      />
      {preview && <img src={preview} alt="Preview" width={200} />}
      <input
        type="number"
        value={price}
        onChange={(e) => setPrice(e.target.value)}
        placeholder="Price (â‚¹)"
        required
      />
      <button type="submit">Post Artwork</button>
    </form>
  );
}
