import { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

export default function Gallery() {
  const [artworks, setArtworks] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/artworks')
      .then(res => setArtworks(res.data))
      .catch(err => console.error(err));
  }, []);

  return (
    <div className="p-6 grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4">
      {artworks.map(art => (
        <Link to={`/art/${art._id}`} key={art._id} className="border p-4 rounded shadow">
          <img src={`http://localhost:5000${art.image}`} alt={art.title} className="w-full h-40 object-cover mb-2" />
          <h3 className="font-semibold">{art.title}</h3>
          <p>â‚¹{art.price}</p>
        </Link>
      ))}
    </div>
  );
}
