import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';

export default function CartPage() {
  const { cartItems, removeFromCart, clearCart } = useCart();
  const [orderMessage, setOrderMessage] = useState('');
  const navigate = useNavigate();
  const [fadeIn, setFadeIn] = useState(false);

  useEffect(() => {
    setFadeIn(true);
  }, []);

  const handlePlaceOrder = async () => {
    if (!cartItems || cartItems.length === 0) {
      setOrderMessage('üõí Your cart is empty.');
      return;
    }

    const token = localStorage.getItem('token');
    if (!token) {
      setOrderMessage('‚ùå You must be logged in to place an order.');
      return;
    }

    const items = cartItems.map((item) => ({
      artwork: item._id,
    }));

    try {
      await axios.post(
        'http://localhost:5000/api/orders/place',
        { items },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setOrderMessage('‚úÖ Order placed successfully!');
      clearCart();
      setTimeout(() => navigate('/my-orders'), 1000);
    } catch (err) {
      console.error('Order Error:', err);
      setOrderMessage(`‚ùå Order failed: ${err.response?.data?.message || 'Server error'}`);
    }
  };

  return (
    <div
      className={`min-h-screen p-6 bg-gradient-to-br from-black via-gray-900 to-black text-white relative overflow-hidden ${
        fadeIn ? 'fade-in' : ''
      }`}
    >
      <div className="absolute inset-0 bg-[radial-gradient(white_1px,transparent_1px)] [background-size:20px_20px] opacity-10 pointer-events-none z-0"></div>

      <h1 className="text-4xl md:text-5xl font-extrabold text-center mb-10 text-cyan-400 drop-shadow-lg animate-gradient">
        üé® Your Art Cart
      </h1>

      {orderMessage && (
        <div className="text-center text-yellow-400 bg-white/10 p-4 rounded-lg mb-6 max-w-2xl mx-auto shadow-lg border border-yellow-500/30">
          {orderMessage}
        </div>
      )}

      {cartItems.length === 0 ? (
        <p className="text-center text-2xl mt-20 text-white">üñºÔ∏è Your cart is empty.</p>
      ) : (
        <div className="max-w-6xl mx-auto space-y-6">
          <div className="grid gap-6 grid-cols-1 sm:grid-cols-2 lg:grid-cols-3">
            {cartItems.map((item) => (
              <div
                key={item._id}
                className="rounded-xl backdrop-blur-lg border border-cyan-400/20 bg-white/5 hover:bg-white/10 shadow-lg transition-all"
              >
                <img
                  src={`http://localhost:5000${item.imageUrl}`}
                  alt={item.title}
                  className="rounded-t-xl w-full h-60 object-cover border-b border-cyan-400/30"
                />
                <div className="p-4 space-y-2">
                  <h2 className="text-lg font-semibold text-cyan-300">{item.title}</h2>
                  <p className="text-white">‚Çπ{item.price}</p>
                  <button
                    onClick={() => removeFromCart(item._id)}
                    className="text-red-400 bg-red-900/40 px-4 py-1 rounded-full text-sm hover:bg-red-800/70 hover:scale-105 transition"
                  >
                    ‚úñ Remove
                  </button>
                </div>
              </div>
            ))}
          </div>

          <div className="flex flex-col sm:flex-row justify-between items-center gap-4 mt-10 p-6 rounded-xl bg-white/10 border border-cyan-600/40 shadow-lg backdrop-blur-lg max-w-3xl mx-auto">
            <h3 className="text-xl font-bold text-cyan-300">
              Total:{' '}
              <span className="text-white">
                ‚Çπ{cartItems.reduce((acc, curr) => acc + parseFloat(curr.price), 0).toFixed(2)}
              </span>
            </h3>

            <button
              onClick={handlePlaceOrder}
              className="relative px-8 py-3 text-white font-semibold rounded-xl bg-gradient-to-r from-cyan-500 to-blue-600 hover:from-blue-600 hover:to-purple-600 shadow-lg hover:shadow-xl transition-all hover:scale-105 place-order-btn"
            >
              üöÄ Place Order
              <span className="absolute inset-0 opacity-0 hover:opacity-100 transition-opacity duration-500"></span>
            </button>
          </div>
        </div>
      )}

      <style>{`
        .fade-in {
          animation: fadeIn 1s ease-in-out;
        }

        .animate-gradient {
          background: linear-gradient(90deg, #0ff, #00f, #0ff);
          background-size: 200% 200%;
          background-clip: text;
          -webkit-background-clip: text;
          color: transparent;
          animation: gradientMove 5s infinite linear;
        }

        @keyframes fadeIn {
          from { opacity: 0; transform: translateY(20px); }
          to { opacity: 1; transform: translateY(0); }
        }

        @keyframes gradientMove {
          0% { background-position: 0% 50%; }
          50% { background-position: 100% 50%; }
          100% { background-position: 0% 50%; }
        }

        .place-order-btn::before {
          content: "";
          position: absolute;
          top: 0;
          left: -75%;
          width: 50%;
          height: 100%;
          background: linear-gradient(
            120deg,
            rgba(255, 255, 255, 0.3) 0%,
            rgba(255, 255, 255, 0.1) 50%,
            rgba(255, 255, 255, 0.3) 100%
          );
          transform: skewX(-20deg);
          transition: left 0.75s ease-in-out;
        }

        .place-order-btn:hover::before {
          left: 130%;
        }
      `}</style>
    </div>
  );
}
