import { Link } from 'react-router-dom';
import axios from 'axios';

const handleDelete = async (id) => {
  const confirmDelete = window.confirm('Are you sure you want to delete this artwork?');
  if (!confirmDelete) return;

  const token = localStorage.getItem('token');
  try {
    await axios.delete(`http://localhost:5000/api/artworks/${id}`, {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
    alert('Artwork deleted!');
    window.location.reload(); // Or use state to remove from UI without reload
  } catch (err) {
    console.error('Delete error:', err);
    alert('Delete failed');
  }
};

