<article key={art._id} className="p-4 border rounded shadow mb-4">
  <h3 className="text-xl font-bold">{art.title}</h3>
  <img src={art.imageUrl} alt={art.title} className="my-2 rounded" width={150} />
  <p>{art.description}</p>
  <p>Price: â‚¹{art.price}</p>
  <div className="space-x-2 mt-2">
    <Link to={`/edit/${art._id}`} className="text-blue-500">Edit</Link>
    <button
      onClick={() => handleDelete(art._id)}
      className="text-red-500 ml-4"
    >
      Delete
    </button>
  </div>
</article>
