// src/pages/EditArtwork.js
import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './EditArtwork.css'; // Import the CSS file

export default function EditArtwork() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [art, setArt] = useState(null);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [price, setPrice] = useState('');
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchArtwork = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/artworks/${id}`);
        setArt(res.data);
        setTitle(res.data.title || '');
        setDescription(res.data.description || '');
        setPrice(res.data.price || '');
      } catch (err) {
        console.error('Error fetching artwork:', err);
        alert('Failed to load artwork.');
        navigate('/');
      }
    };
    fetchArtwork();
  }, [id, navigate]);

  const handleUpdate = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (!token) return alert('You must be logged in to update artwork.');

    setLoading(true);
    try {
      await axios.put(
        `http://localhost:5000/api/artworks/${id}`,
        { title, description, price },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      alert('Artwork updated successfully!');
      navigate('/artgallery');
    } catch (err) {
      console.error('Update error:', err);
      alert('Failed to update artwork.');
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async () => {
    if (!window.confirm('Are you sure you want to delete this artwork?')) return;
    const token = localStorage.getItem('token');
    if (!token) return alert('You must be logged in to delete artwork.');

    try {
      await axios.delete(`http://localhost:5000/api/artworks/${art._id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert('Artwork deleted!');
      navigate('/artgallery');
    } catch (err) {
      console.error('Delete error:', err);
      alert('Failed to delete artwork.');
    }
  };

  if (!art) {
    return (
      <div className="loading-container">
        Loading artwork...
      </div>
    );
  }

  return (
    <div className="edit-artwork-container">
      <h2 className="edit-artwork-heading">
        🎨 Edit Artwork
      </h2>

      <form onSubmit={handleUpdate} className="space-y-6">
        <div>
          <label className="block text-lg font-medium text-gray-700 mb-1">Title</label>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Enter artwork title"
            required
          />
        </div>

        <div>
          <label className="block text-lg font-medium text-gray-700 mb-1">Description</label>
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            rows={4}
            placeholder="Enter description"
            required
          />
        </div>

        <div>
          <label className="block text-lg font-medium text-gray-700 mb-1">Price (₹)</label>
          <input
            type="number"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
            placeholder="Enter price"
            required
          />
        </div>

        <button
          type="submit"
          disabled={loading}
          className="submit-button"
        >
          {loading ? 'Updating...' : '💾 Update Artwork'}
        </button>
      </form>

      <div className="text-center mt-6">
        <button
          onClick={handleDelete}
          className="delete-button"
        >
          🗑️ Delete Artwork
        </button>
      </div>
    </div>
  );
}

/* EditArtwork.css */
const GalleryStyles =`
body {
  background: linear-gradient(to bottom right, #f1e7ff, #b3c7f9, #fbd7ff);
  font-family: 'Arial', sans-serif;
}

/* Card container with backdrop blur effect */
.edit-artwork-container {
  width: 100%;
  max-width: 600px;
  background-color: rgba(255, 255, 255, 0.6);
  backdrop-filter: blur(20px);
  border-radius: 30px;
  box-shadow: 0 20px 40px rgba(0, 0, 0, 0.1);
  padding: 30px;
  transition: transform 0.3s ease-in-out;
}

.edit-artwork-container:hover {
  transform: scale(1.02);
}

/* Heading */
.edit-artwork-heading {
  font-size: 2rem;
  font-weight: 700;
  text-align: center;
  margin-bottom: 20px;
  color: #4c51bf;
}

/* Input and Textarea Fields */
input[type="text"],
textarea,
input[type="number"] {
  width: 100%;
  padding: 15px;
  border: 1px solid #e2e8f0;
  border-radius: 12px;
  font-size: 1rem;
  box-sizing: border-box;
  transition: all 0.3s ease;
}

input[type="text"]:focus,
textarea:focus,
input[type="number"]:focus {
  border-color: #9b63d1;
  outline: none;
  box-shadow: 0 0 10px rgba(150, 81, 209, 0.5);
}

/* Button Styles */
button {
  width: 100%;
  padding: 12px;
  border-radius: 12px;
  font-size: 1.1rem;
  transition: all 0.3s ease;
  border: none;
  cursor: pointer;
}

button:hover {
  transform: translateY(-3px);
}

button:disabled {
  background-color: #d1d5db;
  cursor: not-allowed;
}

.submit-button {
  background: linear-gradient(to right, #6b46c1, #9b63d1);
  color: white;
  font-weight: 600;
}

.delete-button {
  background-color: #e53e3e;
  color: white;
}

.delete-button:hover {
  background-color: #c53030;
}

/* Loading text and other elements */
.loading-container {
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
  font-size: 1.25rem;
  font-weight: 600;
  color: #4a5568;
}
`
