import { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

export default function Gallery() {
  const [artworks, setArtworks] = useState([]);
  const [search, setSearch] = useState('');
  const [minPrice, setMinPrice] = useState('');
  const [maxPrice, setMaxPrice] = useState('');

  const fetchArtworks = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/artworks', {
        params: { search, minPrice, maxPrice },
      });
      setArtworks(res.data);
    } catch (err) {
      console.error('Error loading artworks:', err);
    }
  };

  useEffect(() => {
    fetchArtworks();
  }, []);

  const handleFilter = (e) => {
    e.preventDefault();
    fetchArtworks();
  };

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold mb-4">ðŸŽ¨ Art Gallery</h1>

      <form onSubmit={handleFilter} className="flex gap-2 mb-4">
        <input
          type="text"
          placeholder="Search by title"
          className="border p-2 rounded"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
        <input
          type="number"
          placeholder="Min Price"
          className="border p-2 rounded"
          value={minPrice}
          onChange={(e) => setMinPrice(e.target.value)}
        />
        <input
          type="number"
          placeholder="Max Price"
          className="border p-2 rounded"
          value={maxPrice}
          onChange={(e) => setMaxPrice(e.target.value)}
        />
        <button
          type="submit"
          className="bg-blue-500 text-white px-4 rounded hover:bg-blue-600"
        >
          Filter
        </button>
      </form>

      <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
        {artworks.map((art) => (
          <Link to={`/art/${art._id}`} key={art._id} className="border rounded p-2 shadow hover:shadow-md">
            <img src={art.imageUrl} alt={art.title} className="w-full h-48 object-cover rounded" />
            <h2 className="font-semibold text-lg mt-2">{art.title}</h2>
            <p>â‚¹{art.price}</p>
          </Link>
        ))}
      </div>
    </div>
  );
}
