import { useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';

export default function Forms() {
  return (
    <div>
      <Login />
      <Signup />
    </div>
  );
}

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('customer'); // Default role
  const navigate = useNavigate();

  const handleRoleSelection = (selectedRole) => {
    setRole(selectedRole);
    localStorage.setItem('role', selectedRole);  // Store the selected role
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/login', { username, password });
      
      if (response.status === 200) {
        const { token } = response.data;  // Assume the server returns token
        localStorage.setItem('token', token);  // Store the token
        localStorage.setItem('role', role);  // Store the selected role
        navigate('/upload');  // Redirect to upload page after successful login
      }
    } catch (error) {
      console.error('Login failed:', error);
    }
  };

  return (
    <div>
      <form onSubmit={handleLogin}>
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <div>
          <button type="button" onClick={() => handleRoleSelection('artist')}>Artist</button>
          <button type="button" onClick={() => handleRoleSelection('customer')}>Customer</button>
        </div>
        <button type="submit">Login</button>
      </form>
    </div>
  );
}

function Signup() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('customer');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSignup = async (e) => {
    e.preventDefault();
    setError('');
    try {
      await axios.post('http://localhost:5000/api/auth/signup', {
        name,
        email,
        password,
        role,
      });
      alert('Signup successful! Now log in.');
      navigate('/login');
    } catch (err) {
      setError(err.response?.data?.message || 'Signup failed: Unknown error');
    }
  };

  return (
    <div className="auth-wrapper">
      <style>{formStyles}</style>
      <form onSubmit={handleSignup} className="auth-form">
        <h2 className="form-title">üìù Signup</h2>
        <input
          value={name}
          onChange={(e) => setName(e.target.value)}
          placeholder="Name"
          required
          className="form-input"
        />
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          placeholder="Email"
          required
          className="form-input"
        />
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Password"
          required
          className="form-input"
        />
        <select
          value={role}
          onChange={(e) => setRole(e.target.value)}
          className="form-input"
        >
          <option value="customer">Customer</option>
          <option value="artist">Artist</option>
        </select>
        {error && <div className="form-error">{error}</div>}
        <button type="submit" className="form-button signup">Signup</button>
        <div className="form-footer">
          <span>Already have an account? </span>
          <Link to="/login" className="form-link">Login</Link>
        </div>
      </form>
    </div>
  );
}

const formStyles = `
body {
  background: linear-gradient(135deg, #2a2a35, #121212);
  font-family: 'Times New Roman', serif;
  color: #fff;
}

.auth-wrapper {
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 100vh;
  background: rgba(0, 0, 0, 0.7);
}

.auth-form {
  background: rgb(42, 42, 43);
  padding: 3rem;
  border-radius: 20px;
  box-shadow: 0 4px 25px rgba(0, 255, 255, 0.2);
  max-width: 400px;
  width: 100%;
  backdrop-filter: blur(10px);
}

.form-title {
  text-align: center;
  font-size: 30px;
  margin-bottom: 1.5rem;
  color: #2d3436;
  text-shadow: 0 0 10px #00fff7, 0 0 20px #00fff7, 0 0 30px #00fff7;
}

.form-input {
  width: 100%;
  padding: 14px 16px;
  margin-bottom: 1.2rem;
  border: none;
  border-radius: 8px;
  background: #f3f3f5;
  color: #000;
  font-size: 16px;
  transition: all 0.4s ease;
  font-family: 'Times New Roman', serif;
}

.form-input:focus {
  outline: none;
  box-shadow: 0 0 12px #00fff7;
  background: #e0e0e0;
  color: #000;
}

.form-button {
  width: 100%;
  padding: 14px;
  font-size: 17px;
  font-weight: bold;
  border: none;
  border-radius: 35px;
  cursor: pointer;
  transition: all 0.3s ease;
  letter-spacing: 0.5px;
  font-family: 'Times New Roman', serif;
}

.form-button.login {
  background: linear-gradient(145deg, #00fff7, #00fff7);
  color: white;
}

.form-button.login:hover {
  background: #2d3436;
  box-shadow: 0 0 6px #00fff7, 0 0 10px #00fff7;
}

.form-button.signup {
  background: linear-gradient(145deg, #00fff7, #00fff7);
  color: white;
}

.form-button.signup:hover {
  background: #2d3436;
  box-shadow: 0 0 6px #00fff7, 0 0 10px #00fff7;
}

.form-footer {
  text-align: center;
  margin-top: 1.5rem;
  font-size: 15px;
  color: #ccc;
}

.form-link {
  color: #00fff7;
  text-decoration: none;
  font-weight: bold;
  transition: all 0.3s ease;
}

.form-link:hover {
  text-decoration: underline;
  color: #00fff7;
}

.form-error {
  color: #ff6b6b;
  font-size: 14px;
  text-align: center;
  margin-bottom: 1rem;
}
`;
