import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';

export default function CartPage() {
  const { cart, removeFromCart, clearCart } = useCart();
  const [orderMessage, setOrderMessage] = useState('');
  const navigate = useNavigate();

  const handlePlaceOrder = async () => {
    // Filter valid artworks
    const items = cart
      .filter((item) => item && item._id)
      .map((item) => ({
        artwork: item._id,
      }));

    if (items.length === 0) {
      setOrderMessage('Your cart has invalid items. Cannot place order.');
      return;
    }

    try {
      const res = await axios.post(
        'http://localhost:5000/api/orders/place',
        { items },
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        }
      );
      setOrderMessage('✅ Order placed successfully!');
      clearCart();
      navigate('/orders');
    } catch (err) {
      setOrderMessage(`❌ Order failed: ${err.response?.data?.message || 'Server error'}`);
      console.error('Order failed:', err);
    }
  };

  const totalPrice = cart.reduce((sum, item) => sum + (item.price || 0), 0);

  return (
    <div className="p-6 max-w-3xl mx-auto">
      <h1 className="text-3xl font-bold mb-4">🛒 Your Cart</h1>
      {orderMessage && <p className="text-red-500 mb-4">{orderMessage}</p>}

      {cart.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <div>
          {cart.map((item) => (
            <div key={item._id} className="flex justify-between mb-4 items-center border-b pb-2">
              <div>
                <h2 className="font-semibold">{item.title}</h2>
                <p className="text-gray-600">₹{item.price}</p>
              </div>
              <button
                onClick={() => removeFromCart(item._id)}
                className="text-red-500 hover:underline"
              >
                Remove
              </button>
            </div>
          ))}

          <div className="text-right font-semibold text-lg mt-4">
            Total: ₹{totalPrice}
          </div>

          <button
            onClick={handlePlaceOrder}
            className="mt-6 bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700"
          >
            ✅ Place Order
          </button>
        </div>
      )}
    </div>
  );
}
