// src/pages/Wishlist.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import { useFavorites } from '../context/FavouritesContext';

export default function Wishlist() {
  const [allArtworks, setAllArtworks] = useState([]);
  const { favorites, toggleFavorite } = useFavorites();
  const { addToCart } = useCart();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchAllArtworks = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/artworks');
        setAllArtworks(res.data);
      } catch (err) {
        console.error('Error fetching artworks:', err);
      }
    };
    fetchAllArtworks();
  }, []);

  const wishlistArtworks = allArtworks.filter((art) => favorites[art._id]);

  const handleBuyNow = (art) => {
    addToCart(art);
    navigate('/cart');
  };

  return (
    <div className="gallery-container">
      <style>{WishlistStyles}</style>
      <h1 className="gallery-header" style={{color:' #2d3436',
      textShadow:'0 0 6px #00fff7, 0 0 10px #00fff7',fontSize:'35px'}}>‚ù§Ô∏è My Wishlist</h1>

      {wishlistArtworks.length === 0 ? (
        <p style={{ textAlign: 'center' }}>No favorites added yet.</p>
      ) : (
        <div className="artwork-grid">
          {wishlistArtworks.map((art) => (
            <div key={art._id} className="artwork-card">
              <div className="image-wrapper">
                <img
                  src={`http://localhost:5000${art.imageUrl}`}
                  alt={art.title}
                  className="artwork-image"
                />
              </div>

              <div className="price-tag">‚Çπ{art.price}</div>

              <button
                className={`fav-button active`}
                onClick={() => toggleFavorite(art._id)}
              >
                ‚ù§Ô∏è
              </button>

              <Link to={`/art/${art._id}`}>
                <h2 className="artwork-title">{art.title}</h2>
              </Link>

              <p className="artwork-description">{art.description}</p>

              <button onClick={() => handleBuyNow(art)} className="buy-button">
                üõí Buy Now
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

const WishlistStyles = `
// You can reuse or adjust GalleryStyles here if needed

body{
}
.gallery-container {
  padding: 2rem;
  max-width: 1400px;
  margin: auto;
}

.gallery-header {
  font-size: 2.4rem;
  text-align: center;
  font-weight: 800;
  margin-bottom: 2rem;
}

.artwork-grid {
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(260px, 1fr));
  gap: 32px;
}

.artwork-card {
  background: white;
  border-radius: 20px;
  padding: 1rem;
  position: relative;
  box-shadow: 0 10px 25px rgba(0,0,0,0.1);
  transition: all 0.3s ease;
}

.image-wrapper {
  overflow: hidden;
  border-radius: 15px;
}

.artwork-image {
  width: 100%;
  height: 220px;
  object-fit: cover;
}

.price-tag {
  position: absolute;
  top: 1rem;
  left: 1rem;
  background: #eb4d4b;
  color: white;
  padding: 6px 12px;
  border-radius: 20px;
  font-weight: bold;
}

.fav-button {
  position: absolute;
  top: 1rem;
  right: 1rem;
  background: transparent;
  border: none;
  font-size: 1.4rem;
  cursor: pointer;
}

.artwork-title {
  font-size: 1.3rem;
  margin: 1rem 0 0.5rem;
  font-weight: bold;
}

.artwork-description {
  font-size: 0.95rem;
  color: #555;
  margin-bottom: 1.5rem;
}

.buy-button {
  background: #6c5ce7;
  color: white;
  border: none;
  padding: 10px 20px;
  font-weight: bold;
  border-radius: 30px;
  cursor: pointer;
}
`;
