// src/pages/EditArtwork.js
import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

export default function EditArtwork() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [art, setArt] = useState(null);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [price, setPrice] = useState('');
  const [loading, setLoading] = useState(false);

  // Inject CSS directly into the document head (this is unconditional now)
  useEffect(() => {
    const style = document.createElement('style');
    style.innerHTML = `
      body {
        background: linear-gradient(to bottom right, #e6e9f0, #eef2f3);
        font-family: 'Roboto', sans-serif;
      }

      .edit-artwork-container {
        width: 100%;
        max-width: 750px;
        background-color: rgba(255, 255, 255, 0.9);
        backdrop-filter: blur(15px);
        border-radius: 25px;
        box-shadow: 0 15px 30px rgba(0, 0, 0, 0.1);
        padding: 40px 30px;
        transition: transform 0.3s ease-in-out, box-shadow 0.3s ease-in-out;
        margin: 60px auto;
        border: 1px solid #ddd;
      }

      .edit-artwork-container:hover {
        transform: scale(1.03);
        box-shadow: 0 20px 40px rgba(0, 0, 0, 0.2);
      }

      .edit-artwork-heading {
        font-size: 2.8rem;
        font-weight: 800;
        text-align: center;
        margin-bottom: 25px;
        color: #6c63ff;
        text-shadow: 3px 3px 5px rgba(0, 0, 0, 0.2);
      }

      .form-group {
        margin-bottom: 25px;
      }

      label {
        font-size: 1.1rem;
        font-weight: 600;
        color: #2d3748;
        margin-bottom: 8px;
      }

      input[type="text"],
      textarea,
      input[type="number"] {
        width: 100%;
        padding: 15px;
        border: 1px solid #e2e8f0;
        border-radius: 12px;
        font-size: 1.1rem;
        box-sizing: border-box;
        transition: all 0.3s ease;
      }

      input[type="text"]:focus,
      textarea:focus,
      input[type="number"]:focus {
        border-color: #9b63d1;
        outline: none;
        box-shadow: 0 0 12px rgba(150, 81, 209, 0.4);
        transform: scale(1.02);
      }

      .submit-button {
        width: 100%;
        padding: 15px;
        border-radius: 12px;
        font-size: 1.2rem;
        background: linear-gradient(to right, #6b46c1, #9b63d1);
        color: white;
        font-weight: 600;
        border: none;
        cursor: pointer;
        box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
        transition: transform 0.3s ease, box-shadow 0.3s ease;
      }

      .submit-button:hover {
        transform: translateY(-3px);
        box-shadow: 0 8px 20px rgba(0, 0, 0, 0.2);
        background: linear-gradient(to right, #9b63d1, #6b46c1);
      }

      .submit-button:disabled {
        background-color: #d1d5db;
        cursor: not-allowed;
      }

      .delete-button {
        width: 100%;
        padding: 15px;
        border-radius: 12px;
        font-size: 1.2rem;
        background-color: #e53e3e;
        color: white;
        font-weight: 600;
        border: none;
        cursor: pointer;
        margin-top: 20px;
        transition: transform 0.3s ease, box-shadow 0.3s ease;
      }

      .delete-button:hover {
        transform: translateY(-3px);
        box-shadow: 0 8px 20px rgba(0, 0, 0, 0.2);
        background-color: #c53030;
      }

      .loading-container {
        display: flex;
        justify-content: center;
        align-items: center;
        height: 100vh;
        font-size: 1.5rem;
        font-weight: 600;
        color: #4a5568;
      }

      .form-group input,
      .form-group textarea {
        transition: all 0.4s ease-in-out;
      }

      .form-group input:focus,
      .form-group textarea:focus {
        transform: scale(1.02);
      }

      /* Input field hover effect */
      .form-group input:hover,
      .form-group textarea:hover {
        border-color: #9b63d1;
        box-shadow: 0 0 10px rgba(150, 81, 209, 0.3);
      }

      /* Card shadow effect on hover */
      .edit-artwork-container {
        box-shadow: 0 10px 30px rgba(0, 0, 0, 0.2);
        transition: box-shadow 0.3s ease-in-out;
      }

      .edit-artwork-container:hover {
        box-shadow: 0 20px 60px rgba(0, 0, 0, 0.3);
      }

      /* Additional styling for error/validation messages */
      .error-message {
        color: #e53e3e;
        font-weight: bold;
        text-align: center;
        margin-top: 20px;
        font-size: 1.2rem;
      }
    `;
    document.head.appendChild(style);

    // Cleanup on unmount
    return () => {
      document.head.removeChild(style);
    };
  }, []); // Empty dependency array ensures this runs only once

  useEffect(() => {
    const fetchArtwork = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/artworks/${id}`);
        setArt(res.data);
        setTitle(res.data.title || '');
        setDescription(res.data.description || '');
        setPrice(res.data.price || '');
      } catch (err) {
        console.error('Error fetching artwork:', err);
        alert('Failed to load artwork.');
        navigate('/');
      }
    };
    fetchArtwork();
  }, [id, navigate]);

  const handleUpdate = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (!token) return alert('You must be logged in to update artwork.');

    setLoading(true);
    try {
      await axios.put(
        `http://localhost:5000/api/artworks/${id}`,
        { title, description, price },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      alert('Artwork updated successfully!');
      navigate('/artgallery');
    } catch (err) {
      console.error('Update error:', err);
      alert('Failed to update artwork.');
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async () => {
    if (!window.confirm('Are you sure you want to delete this artwork?')) return;
    const token = localStorage.getItem('token');
    if (!token) return alert('You must be logged in to delete artwork.');

    try {
      await axios.delete(`http://localhost:5000/api/artworks/${art._id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert('Artwork deleted!');
      navigate('/artgallery');
    } catch (err) {
      console.error('Delete error:', err);
      alert('Failed to delete artwork.');
    }
  };

  if (!art) {
    return (
      <div className="loading-container">
        Loading artwork...
      </div>
    );
  }

  return (
    <div className="edit-artwork-container">
      <h2 className="edit-artwork-heading">
        🎨 Edit Artwork
      </h2>

      <form onSubmit={handleUpdate}>
        <div className="form-group">
          <label>Title</label>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Enter artwork title"
            required
          />
        </div>

        <div className="form-group">
          <label>Description</label>
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            rows={4}
            placeholder="Enter description"
            required
          />
        </div>

        <div className="form-group">
          <label>Price (₹)</label>
          <input
            type="number"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
            placeholder="Enter price"
            required
          />
        </div>

        <button
          type="submit"
          disabled={loading}
          className="submit-button"
        >
          {loading ? 'Updating...' : '💾 Update Artwork'}
        </button>
      </form>

      <div className="text-center">
        <button
          onClick={handleDelete}
          className="delete-button"
        >
          🗑️ Delete Artwork
        </button>
      </div>
    </div>
  );
}
