// src/pages/ArtDetails.js
import { useParams } from 'react-router-dom';
import { useEffect, useState } from 'react';
import axios from 'axios';

export default function ArtDetails() {
  const { id } = useParams();
  const [art, setArt] = useState(null);
  const [buyerId, setBuyerId] = useState('');
  const [isSold, setIsSold] = useState(false);

  useEffect(() => {
    axios.get(`http://localhost:5000/api/artworks/${id}`)
      .then(res => {
        setArt(res.data);
        setIsSold(res.data.sold);
      });
  }, [id]);
  
  const handleBuy = async () => {
    try {
      await axios.patch(`http://localhost:5000/api/artworks/${id}/buy`, {
        buyerId,
      });
      alert("Purchase successful!");
      setIsSold(true);
    } catch (err) {
      alert("Purchase failed!");
    }
  };
  

  return (
    <div>
      <img src={art.image} alt={art.title} width="400" />
      <h2>{art.title}</h2>
      <p>Uploaded by: {art.userId}</p>
      
    </div>
  );
}
