import { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import jwt_decode from 'jwt-decode';

export default function Forms() {
  const [isLogin, setIsLogin] = useState(true);
  const [form, setForm] = useState({ name: '', email: '', password: '', role: 'customer' });
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const toggleForm = () => {
    setIsLogin(!isLogin);
    setForm({ name: '', email: '', password: '', role: 'customer' });
    setError('');
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    if (!form.email || !form.password || (!isLogin && !form.name)) {
      setError('All fields are required');
      return;
    }

    try {
      if (isLogin) {
        const res = await axios.post('http://localhost:5000/api/auth/login', {
          email: form.email,
          password: form.password,
        });
        const token = res.data.token;
        const decoded = jwt_decode(token);
        localStorage.setItem('token', token);
        localStorage.setItem('role', decoded.role);

        if (decoded.role === 'admin') navigate('/admin/dashboard');
        else if (decoded.role === 'artist') navigate('/artist/dashboard');
        else navigate('/customer/home');
      } else {
        await axios.post('http://localhost:5000/api/auth/signup', form);
        alert('Signup successful! Please login.');
        setIsLogin(true);
      }
    } catch (err) {
      setError(err.response?.data?.message || 'Something went wrong');
    }
  };

  return (
    <div className="auth-wrapper">
      <style>{formStyles}</style>
      <form onSubmit={handleSubmit} className="auth-form">
        <h2 className="form-title">{isLogin ? '🔐 Login' : `📝 Signup as ${form.role}`}</h2>

        {!isLogin && (
          <input
            name="name"
            value={form.name}
            onChange={handleChange}
            placeholder="Name"
            className="form-input"
            required
          />
        )}

        <input
          name="email"
          type="email"
          value={form.email}
          onChange={handleChange}
          placeholder="Email"
          className="form-input"
          required
        />
        <input
          name="password"
          type="password"
          value={form.password}
          onChange={handleChange}
          placeholder="Password"
          className="form-input"
          required
        />

        {!isLogin && (
          <select name="role" value={form.role} onChange={handleChange} className="form-input">
            <option value="customer">Customer</option>
            <option value="artist">Artist</option>
            <option value="admin">Admin</option>
          </select>
        )}

        {error && <div className="form-error">{error}</div>}

        <button type="submit" className={`form-button ${isLogin ? 'login' : 'signup'}`}>
          {isLogin ? 'Login' : 'Signup'}
        </button>

        <div className="form-footer">
          <span>{isLogin ? "Don't have an account?" : 'Already have an account?'}</span>
          <button type="button" onClick={toggleForm} className="form-link">
            {isLogin ? 'Signup' : 'Login'}
          </button>
        </div>
      </form>
    </div>
  );
}

const formStyles = `
body {
  background: linear-gradient(135deg, #2a2a35, #121212);
  font-family: 'Times New Roman', serif;
  color: #fff;
}

.auth-wrapper {
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 100vh;
  background: rgba(0, 0, 0, 0.7);
}

.auth-form {
  background: rgb(42, 42, 43);
  padding: 3rem;
  border-radius: 20px;
  box-shadow: 0 4px 25px rgba(0, 255, 255, 0.2);
  max-width: 400px;
  width: 100%;
  backdrop-filter: blur(10px);
}

.form-title {
  text-align: center;
  font-size: 30px;
  margin-bottom: 1.5rem;
  color: #2d3436;
  text-shadow: 0 0 10px #00fff7, 0 0 20px #00fff7, 0 0 30px #00fff7;
}

.form-input {
  width: 100%;
  padding: 14px 16px;
  margin-bottom: 1.2rem;
  border: none;
  border-radius: 8px;
  background: #f3f3f5;
  color: #000;
  font-size: 16px;
  transition: all 0.4s ease;
  font-family: 'Times New Roman', serif;
}

.form-input:focus {
  outline: none;
  box-shadow: 0 0 12px #00fff7;
  background: #e0e0e0;
  color: #000;
}

.form-button {
  width: 100%;
  padding: 14px;
  font-size: 17px;
  font-weight: bold;
  border: none;
  border-radius: 35px;
  cursor: pointer;
  transition: all 0.3s ease;
  letter-spacing: 0.5px;
  font-family: 'Times New Roman', serif;
}

.form-button.login,
.form-button.signup {
  background: linear-gradient(145deg, #00fff7, #00fff7);
  color: white;
}

.form-button:hover {
  background: #2d3436;
  box-shadow: 0 0 6px #00fff7, 0 0 10px #00fff7;
}

.form-footer {
  text-align: center;
  margin-top: 1.5rem;
  font-size: 15px;
  color: #ccc;
}

.form-link {
  background: none;
  border: none;
  color: #00fff7;
  font-weight: bold;
  cursor: pointer;
  margin-left: 5px;
  text-decoration: underline;
}

.form-error {
  color: #ff6b6b;
  font-size: 14px;
  text-align: center;
  margin-bottom: 1rem;
}
`;
