import { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

export default function Upload() {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [price, setPrice] = useState('');
  const [imageFile, setImageFile] = useState(null);
  const [preview, setPreview] = useState('');
  const [loading, setLoading] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(true);

  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) setIsLoggedIn(false);
  }, []);

  const handleImageUpload = async () => {
    const formData = new FormData();
    formData.append('image', imageFile);
    const res = await axios.post('http://localhost:5000/api/upload/upload', formData);
    return res.data.imageUrl;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (!token) {
      alert('You must be logged in.');
      return;
    }

    try {
      setLoading(true);
      const imageUrl = await handleImageUpload();

      await axios.post(
        'http://localhost:5000/api/artworks',
        { title, description, price, imageUrl },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      alert('Artwork uploaded successfully!');
      navigate('/artgallery');
    } catch (err) {
      console.error(err);
      alert('Upload failed!');
    } finally {
      setLoading(false);
    }
  };

  if (!isLoggedIn) {
    return (
      <div className="text-center mt-10 p-6 text-red-700 font-semibold text-lg bg-red-100 border border-red-300 rounded-xl shadow-lg animate-pulse">
        ðŸš« Please login to upload your artwork.
      </div>
    );
  }

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-indigo-100 via-pink-100 to-yellow-50 p-6">
      <div className="w-full max-w-xl bg-white/40 backdrop-blur-lg border border-white/20 rounded-3xl p-10 shadow-[0_10px_60px_rgba(0,0,0,0.1)] transition-transform duration-300 hover:scale-[1.02] hover:shadow-2xl">

        <h2 className="text-4xl font-bold text-center text-purple-700 mb-8 font-serif tracking-wide drop-shadow">
          âœ¨ Upload Artwork
        </h2>

        <form onSubmit={handleSubmit} className="space-y-6">
          {/* Title */}
          <div className="card-field">
            <label className="block text-sm font-medium text-gray-700 mb-1">Title</label>
            <input
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              required
              placeholder="Artwork Title"
              className="w-full px-4 py-3 rounded-xl border border-purple-200 shadow-inner bg-white/90 focus:outline-none focus:ring-2 focus:ring-purple-400"
            />
          </div>

          {/* Description */}
          <div className="card-field">
            <label className="block text-sm font-medium text-gray-700 mb-1">Description</label>
            <textarea
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              required
              placeholder="Describe your art"
              className="w-full px-4 py-3 rounded-xl border border-purple-200 shadow-inner bg-white/90 focus:outline-none focus:ring-2 focus:ring-purple-400"
            />
          </div>

          {/* Price */}
          <div className="card-field">
            <label className="block text-sm font-medium text-gray-700 mb-1">Price (â‚¹)</label>
            <input
              type="number"
              value={price}
              onChange={(e) => setPrice(e.target.value)}
              required
              placeholder="Eg. 1499"
              className="w-full px-4 py-3 rounded-xl border border-purple-200 shadow-inner bg-white/90 focus:outline-none focus:ring-2 focus:ring-purple-400"
            />
          </div>

          {/* File Upload */}
          <div className="card-field">
            <label className="block text-sm font-medium text-gray-700 mb-1">Upload Image</label>
            <input
              type="file"
              accept="image/*"
              onChange={(e) => {
                setImageFile(e.target.files[0]);
                setPreview(URL.createObjectURL(e.target.files[0]));
              }}
              required
              className="w-full cursor-pointer text-sm file:mr-4 file:py-2 file:px-4 file:rounded-full
                         file:border-0 file:font-semibold
                         file:bg-purple-600 file:text-white hover:file:bg-purple-700 transition-all"
            />
          </div>

          {/* Preview */}
          {preview && (
            <div className="flex justify-center">
              <img
                src={preview}
                alt="Preview"
                className="mt-4 max-h-64 rounded-2xl border shadow-xl transition-transform duration-300 hover:scale-105"
              />
            </div>
          )}

          {/* Submit */}
          <button
            type="submit"
            disabled={loading}
            className="w-full py-3 text-lg font-semibold text-white bg-gradient-to-r from-purple-600 to-pink-500 rounded-full shadow-md hover:shadow-xl transition duration-300"
          >
            {loading ? 'Uploading...' : 'ðŸš€ Upload'}
          </button>
        </form>
      </div>
    </div>
  );
}
