import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

export default function EditArtwork() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [art, setArt] = useState(null);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [price, setPrice] = useState('');
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchArtwork = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/artworks/${id}`);
        setArt(res.data);
        setTitle(res.data.title || '');
        setDescription(res.data.description || '');
        setPrice(res.data.price || '');
      } catch (err) {
        console.error('Error fetching artwork:', err);
        alert('Failed to load artwork.');
        navigate('/');
      }
    };
    fetchArtwork();
  }, [id, navigate]);

  const handleUpdate = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (!token) return alert('You must be logged in to update artwork.');

    setLoading(true);
    try {
      await axios.put(
        `http://localhost:5000/api/artworks/${id}`,
        { title, description, price },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      alert('Artwork updated successfully!');
      navigate('/artgallery');
    } catch (err) {
      console.error('Update error:', err);
      alert('Failed to update artwork.');
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async () => {
    if (!window.confirm('Are you sure you want to delete this artwork?')) return;
    const token = localStorage.getItem('token');
    if (!token) return alert('You must be logged in to delete artwork.');

    try {
      await axios.delete(`http://localhost:5000/api/artworks/${art._id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert('Artwork deleted!');
      navigate('/artgallery');
    } catch (err) {
      console.error('Delete error:', err);
      alert('Failed to delete artwork.');
    }
  };

  if (!art) return <div className="p-6 text-center text-lg font-medium animate-pulse">🎨 Loading artwork...</div>;

  return (
    <div className="min-h-screen bg-gradient-to-br from-[#f8fafc] to-[#e0e7ff] py-12 px-6">
      <div className="max-w-xl mx-auto bg-white bg-opacity-80 backdrop-blur-lg p-8 rounded-2xl shadow-xl border border-indigo-200">
        <h2 className="text-3xl font-extrabold text-indigo-700 mb-6 text-center">✏️ Edit Your Artwork</h2>

        <form onSubmit={handleUpdate} className="space-y-5">
          <input
            className="w-full p-3 rounded-xl border border-gray-300 focus:outline-none focus:ring-2 focus:ring-indigo-400"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Artwork Title"
            required
          />

          <textarea
            className="w-full p-3 rounded-xl border border-gray-300 focus:outline-none focus:ring-2 focus:ring-indigo-400"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Description"
            rows={4}
            required
          />

          <input
            type="number"
            className="w-full p-3 rounded-xl border border-gray-300 focus:outline-none focus:ring-2 focus:ring-indigo-400"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
            placeholder="Price in ₹"
            required
          />

          <button
            type="submit"
            disabled={loading}
            className="w-full py-3 rounded-xl bg-indigo-600 text-white font-bold hover:bg-indigo-700 transition duration-300"
          >
            {loading ? 'Updating...' : '💾 Update Artwork'}
          </button>
        </form>

        <div className="text-center mt-6">
          <button
            onClick={handleDelete}
            className="px-5 py-2 bg-red-600 hover:bg-red-700 text-white rounded-full font-semibold transition duration-300"
          >
            🗑️ Delete Artwork
          </button>
        </div>
      </div>
    </div>
  );
}
