import { useCart } from '../context/CartContext';

export default function Cart() {
  const { cartItems, removeFromCart } = useCart();

  const total = cartItems.reduce((sum, item) => sum + Number(item.price), 0);

  return (
    <div className="p-8">
      <h2 className="text-2xl font-bold mb-4">Your Cart</h2>
      {cartItems.length === 0 ? (
        <p>No items in cart.</p>
      ) : (
        <div>
          {cartItems.map((item) => (
            <div key={item._id} className="flex items-center gap-4 border p-4 mb-4">
              <img src={item.imageUrl} alt={item.title} className="w-24 h-24 object-cover" />
              <div className="flex-grow">
                <h3 className="text-lg font-semibold">{item.title}</h3>
                <p>₹{item.price}</p>
              </div>
              <button onClick={() => removeFromCart(item._id)} className="text-red-500 hover:underline">Remove</button>
            </div>
          ))}
          <div className="text-right text-xl mt-4">Total: ₹{total}</div>
        </div>
      )}
    </div>
  );
}
