import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';

export default function Gallery() {
  const [artworks, setArtworks] = useState([]);
  const [search, setSearch] = useState('');
  const [minPrice, setMinPrice] = useState('');
  const [maxPrice, setMaxPrice] = useState('');
  const { addToCart } = useCart();
  const navigate = useNavigate();

  const fetchArtworks = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/artworks', {
        params: { search, minPrice, maxPrice },
      });
      setArtworks(res.data);
    } catch (err) {
      console.error('Error loading artworks:', err);
    }
  };

  useEffect(() => {
    fetchArtworks();
  }, []);

  const handleFilter = (e) => {
    e.preventDefault();
    fetchArtworks();
  };

  const handleAddToCart = async (art) => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('Please log in to add items to cart.');
      return;
    }

    try {
      await axios.post(
        'http://localhost:5000/api/cart',
        { artworkId: art._id },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      addToCart(art); // Frontend cart context
      alert('Artwork added to cart!');
    } catch (err) {
      console.error('Error adding to cart:', err);
      alert('Failed to add to cart.');
    }
  };

  const handleBuyNow = async (art) => {
    await handleAddToCart(art);
    navigate('/cart');
  };

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold mb-4">ðŸŽ¨ Art Gallery</h1>

      <form onSubmit={handleFilter} className="flex flex-wrap gap-2 mb-6">
        <input type="text" placeholder="Search by title" className="border p-2 rounded" value={search} onChange={(e) => setSearch(e.target.value)} />
        <input type="number" placeholder="Min Price" className="border p-2 rounded" value={minPrice} onChange={(e) => setMinPrice(e.target.value)} />
        <input type="number" placeholder="Max Price" className="border p-2 rounded" value={maxPrice} onChange={(e) => setMaxPrice(e.target.value)} />
        <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded">Filter</button>
      </form>

      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
        {artworks.map((art) => (
          <div key={art._id} className="border rounded p-3 shadow bg-white">
            <img src={art.imageUrl} alt={art.title} className="w-full h-48 object-cover rounded" />
            <h2 className="text-lg font-semibold mt-2 text-blue-600">{art.title}</h2>
            <p>â‚¹{art.price}</p>
            <p className="text-sm">{art.description}</p>
            <div className="mt-2 space-x-2">
              <button onClick={() => handleAddToCart(art)} className="bg-green-500 text-white px-3 py-1 rounded">Add to Cart</button>
              <button onClick={() => handleBuyNow(art)} className="bg-yellow-500 text-white px-3 py-1 rounded">Buy Now</button>
            </div>
            <Link to={`/edit/${art._id}`} className="block mt-2 text-xs text-blue-400 hover:underline">Edit</Link>
          </div>
        ))}
      </div>
    </div>
  );
}
