// src/components/ArtGallery.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ArtGallery = () => {
  const [artworks, setArtworks] = useState([]);

  useEffect(() => {
    const fetchArtworks = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/artworks'); // ✅ fixed endpoint
        setArtworks(res.data);
      } catch (err) {
        console.error('Error fetching artworks:', err);
      }
    };

    fetchArtworks();
  }, []);

  const addToCart = (art) => {
    let cart = JSON.parse(localStorage.getItem('cart')) || [];
    cart.push(art);
    localStorage.setItem('cart', JSON.stringify(cart));
    alert(`${art.title} added to cart!`);
  };

  return (
    <div className="p-8 grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
      {artworks.map((art) => (
        <div key={art._id} className="border rounded shadow p-4 bg-white">
          <img src={art.image} alt={art.title} className="w-full h-48 object-cover rounded" />
          <h2 className="text-xl font-semibold mt-2">{art.title}</h2>
          <p className="text-gray-600">{art.description}</p>
          <p className="text-blue-600 font-bold mt-2">₹{art.price}</p>
          <p className="text-sm text-gray-400 mt-1">By: {art.artistId?.name || 'Unknown Artist'}</p>
          <button
            onClick={() => addToCart(art)}
            className="mt-3 px-4 py-2 bg-green-500 text-white rounded hover:bg-green-600"
          >
            Buy
          </button>
        </div>
      ))}
    </div>
  );
};

export default ArtGallery;
