// src/pages/EditArtwork.js
import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

export default function EditArtwork() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [art, setArt] = useState(null);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [price, setPrice] = useState('');
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchArtwork = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/artworks/${id}`);
        setArt(res.data);
        setTitle(res.data.title || '');
        setDescription(res.data.description || '');
        setPrice(res.data.price || '');
      } catch (err) {
        console.error('Error fetching artwork:', err);
        alert('Failed to load artwork.');
        navigate('/'); // fallback
      }
    };

    fetchArtwork();
  }, [id, navigate]);

  const handleUpdate = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (!token) return alert('You must be logged in to update artwork.');

    setLoading(true);
    try {
      await axios.put(
        `http://localhost:5000/api/artworks/${id}`,
        { title, description, price },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      alert('Artwork updated successfully!');
      navigate('/artgallery');
    } catch (err) {
      console.error('Update error:', err);
      alert('Failed to update artwork.');
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async () => {
    if (!window.confirm('Are you sure you want to delete this artwork?')) return;
    const token = localStorage.getItem('token');
    if (!token) return alert('You must be logged in to delete artwork.');

    try {
      await axios.delete(`http://localhost:5000/api/artworks/${art._id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert('Artwork deleted!');
      navigate('/artgallery');
    } catch (err) {
      console.error('Delete error:', err);
      alert('Failed to delete artwork.');
    }
  };

  if (!art) return <div className="p-6 text-center">Loading artwork...</div>;

  return (
    <>
      <div className="max-w-md mx-auto mt-10 p-4 border rounded shadow">
        <h2 className="text-2xl font-bold mb-4">Edit Artwork</h2>
        <form onSubmit={handleUpdate} className="space-y-4">
          <input
            className="w-full p-2 border rounded"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Title"
            required
          />
          <textarea
            className="w-full p-2 border rounded"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Description"
            required
          />
          <input
            type="number"
            className="w-full p-2 border rounded"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
            placeholder="Price"
            required
          />
          <button
            type="submit"
            disabled={loading}
            className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
          >
            {loading ? 'Updating...' : 'Update Artwork'}
          </button>
        </form>
      </div>

      <div className="text-center mt-4">
        <button
          onClick={handleDelete}
          className="bg-red-600 text-white px-3 py-1 rounded hover:bg-red-700"
        >
          Delete
        </button>
      </div>
    </>
  );
}
