import { Link } from 'react-router-dom';
import { useLocation } from 'react-router-dom';

const Navbar = () => {
  const location = useLocation();

  const handleLogout = () => {
    localStorage.removeItem('token');
    window.location.href = '/admin';
  };

  return (
    <>
      <nav className="navbar">
        <Link to="/artgallery" className="navbar-logo">Art Gallery</Link>
        <ul className="nav-links">
          <li><Link to="/" className={location.pathname === '/' ? 'active' : ''}>Home</Link></li>
          <li><Link to="/upload" className={location.pathname === '/upload' ? 'active' : ''}>Upload</Link></li>
          <li><Link to="/my-orders" className={location.pathname === '/my-orders' ? 'active' : ''}>My Orders</Link></li>
          <li><Link to="/login" className={location.pathname === '/login' ? 'active' : ''}>Login</Link></li>
          <li><Link to="/signup" className={location.pathname === '/signup' ? 'active' : ''}>Signup</Link></li>
          <li><Link to="/profile" className={location.pathname === '/profile' ? 'active' : ''}>Profile</Link></li>
          <li>
            <button onClick={handleLogout} className="logout-btn">Logout</button>
          </li>
        </ul>
      </nav>

      <style>{`
        .navbar {
          background: linear-gradient(90deg, rgba(37,99,235,1) 0%, rgba(59,130,246,1) 100%);
          color: white;
          padding: 1rem;
          display: flex;
          justify-content: space-between;
          align-items: center;
          box-shadow: 0 4px 6px rgba(0,0,0,0.1);
          transition: background 0.5s ease-in-out;
        }

        .navbar:hover {
          background: linear-gradient(90deg, rgba(37,99,235,1) 0%, rgba(59,130,246,1) 70%, rgba(99,102,241,1) 100%);
        }

        .navbar-logo {
          font-weight: bold;
          font-size: 1.75rem;
          text-decoration: none;
          color: white;
          letter-spacing: 1.5px;
          transition: color 0.3s ease;
        }

        .navbar-logo:hover {
          color: #cbd5e1;
        }

        .nav-links {
          display: flex;
          gap: 2rem;
          align-items: center;
          list-style: none;
          padding: 0;
          margin: 0;
        }

        .nav-links a {
          color: white;
          text-decoration: none;
          font-size: 1.1rem;
          transition: color 0.3s ease, transform 0.3s ease, padding 0.3s ease;
        }

        .nav-links a:hover {
          color: #cbd5e1;
          transform: scale(1.05);
          padding-bottom: 5px;
          border-bottom: 2px solid #cbd5e1;
        }

        .nav-links .active {
          color: #cbd5e1;
          font-weight: bold;
          text-decoration: underline;
        }

        .logout-btn {
          background-color: #ef4444;
          color: white;
          padding: 0.6rem 1.25rem;
          border-radius: 0.375rem;
          border: none;
          cursor: pointer;
          font-weight: bold;
          letter-spacing: 1px;
          transition: background-color 0.3s ease, transform 0.3s ease, box-shadow 0.3s ease;
        }

        .logout-btn:hover {
          background-color: #dc2626;
          transform: scale(1.05);
          box-shadow: 0 4px 6px rgba(0,0,0,0.2);
        }

        @media (max-width: 768px) {
          .nav-links {
            display: block;
            text-align: center;
          }

          .nav-links a {
            margin: 0.5rem 0;
            font-size: 1.2rem;
          }

          .navbar {
            flex-direction: column;
          }
        }
      `}</style>
    </>
  );
};

export default Navbar;
