import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Upload = () => {
  const [form, setForm] = useState({
    title: '',
    description: '',
    price: '',
    imageUrl: ''
  });
  const [token, setToken] = useState('');
  const [userRole, setUserRole] = useState('');
  const [message, setMessage] = useState('');

  useEffect(() => {
    const storedToken = localStorage.getItem('token');
    if (!storedToken) {
      setMessage('Please login first.');
      return;
    }

    try {
      const payload = JSON.parse(atob(storedToken.split('.')[1]));
      setUserRole(payload.role);
      setToken(storedToken);
    } catch (err) {
      console.error('Invalid token:', err);
      setMessage('Invalid or expired session. Please log in again.');
    }
  }, []);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async () => {
    if (userRole !== 'artist') {
      setMessage('Only artists are allowed to upload artworks.');
      return;
    }

    try {
      const res = await axios.post('http://localhost:5000/api/artworks', form, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMessage('Artwork uploaded successfully.');
      setForm({ title: '', description: '', price: '', imageUrl: '' });
    } catch (err) {
      console.error(err);
      setMessage(err.response?.data?.message || 'Failed to upload artwork.');
    }
  };

  return (
    <div style={styles.container}>
      <h2>Upload Artwork</h2>
      {userRole !== 'artist' ? (
        <p style={styles.error}>{message || 'You are not allowed to upload artwork.'}</p>
      ) : (
        <>
          <input
            type="text"
            name="title"
            placeholder="Title"
            value={form.title}
            onChange={handleChange}
            style={styles.input}
          />
          <input
            type="text"
            name="description"
            placeholder="Description"
            value={form.description}
            onChange={handleChange}
            style={styles.input}
          />
          <input
            type="number"
            name="price"
            placeholder="Price"
            value={form.price}
            onChange={handleChange}
            style={styles.input}
          />
          <input
            type="text"
            name="imageUrl"
            placeholder="Image URL"
            value={form.imageUrl}
            onChange={handleChange}
            style={styles.input}
          />
          <button onClick={handleSubmit} style={styles.button}>
            Upload
          </button>
          {message && <p style={styles.success}>{message}</p>}
        </>
      )}
    </div>
  );
};

const styles = {
  container: {
    maxWidth: '500px',
    margin: '50px auto',
    padding: '20px',
    border: '1px solid #ddd',
    borderRadius: '10px',
    background: '#fafafa',
  },
  input: {
    width: '100%',
    padding: '10px',
    margin: '10px 0',
    borderRadius: '6px',
    border: '1px solid #ccc',
  },
  button: {
    width: '100%',
    padding: '12px',
    backgroundColor: '#4CAF50',
    color: 'white',
    border: 'none',
    borderRadius: '6px',
    cursor: 'pointer',
    fontWeight: 'bold',
  },
  error: {
    color: 'red',
    fontWeight: 'bold',
  },
  success: {
    marginTop: '10px',
    color: 'green',
    fontWeight: 'bold',
  },
};

export default Upload;
