import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';
import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css';

export default function Home() {
  return (
    <>
      <div className="home relative overflow-hidden">
        {/* ✨ Background floating blobs */}
        <div className="absolute top-0 left-0 w-full h-full -z-10 pointer-events-none">
          <div className="absolute bg-pink-500 opacity-20 w-80 h-80 rounded-full mix-blend-multiply filter blur-2xl animate-blob top-20 left-10"></div>
          <div className="absolute bg-yellow-500 opacity-20 w-80 h-80 rounded-full mix-blend-multiply filter blur-2xl animate-blob animation-delay-2000 top-40 left-1/2"></div>
          <div className="absolute bg-blue-500 opacity-20 w-80 h-80 rounded-full mix-blend-multiply filter blur-2xl animate-blob animation-delay-4000 top-0 right-10"></div>
        </div>

        {/* Header */}
        <div className="bg-blue-900 text-white py-14 backdrop-blur-sm bg-opacity-80">
          <div className="text-center space-y-6">
            <h1 className="text-5xl font-bold tracking-wide animate-fadeIn">
              🎨 Welcome to <span className="text-yellow-300">Yuvi's Artistry</span>
            </h1>
            <div className="flex justify-center space-x-6 flex-wrap">
              <Button variant="primary" className="btn-custom-glow">Admin</Button>
              <Button variant="secondary" className="btn-custom-glow">Artist</Button>
              <Button variant="success" className="btn-custom-glow">Customer</Button>
            </div>
          </div>
        </div>

        {/* Carousel */}
        <div className="max-w-6xl mx-auto px-4 py-12 animate-slideIn">
          <Carousel
            autoPlay
            infiniteLoop
            showThumbs={false}
            showStatus={false}
            interval={3000}
            transitionTime={800}
            className="rounded-xl overflow-hidden shadow-2xl"
          >
            {[
              { src: "/images/art1.jpg", title: "Art Title 1", price: "₹999" },
              { src: "/images/art2.jpg", title: "Art Title 2", price: "₹1499" },
              { src: "/images/art3.jpg", title: "Art Title 3", price: "₹1999" },
              { src: "/images/art4.jpeg", title: "Art Title 4", price: "₹2499" },
              { src: "/images/art5.jpeg", title: "Art Title 5", price: "₹2999" },
            ].map((art, i) => (
              <div key={i}>
                <img src={art.src} alt={art.title} className="rounded-xl transform hover:scale-105 transition duration-500" />
                <p className="legend text-lg bg-opacity-70 bg-black text-white p-2">{`${art.title} - ${art.price}`}</p>
              </div>
            ))}
          </Carousel>
        </div>
      </div>

      {/* Styles */}
      <style>{`
        .home {
          min-height: 100vh;
          background-color: #000000;
          font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
        }

        .btn-custom-glow {
          padding: 14px 28px;
          font-size: 1.1rem;
          font-weight: bold;
          border-radius: 10px;
          border: none;
          color: white !important;
          background: linear-gradient(45deg, #ff416c, #ff4b2b);
          box-shadow: 0 0 10px rgba(255, 65, 108, 0.7);
          transition: all 0.3s ease;
        }

        .btn-custom-glow:hover {
          transform: translateY(-3px) scale(1.05);
          box-shadow: 0 0 20px rgba(255, 75, 43, 0.9);
        }

        .carousel .legend {
          font-size: 1.25rem;
          font-weight: bold;
          border-radius: 12px;
          background: rgba(0, 0, 0, 0.6);
        }

        @keyframes blob {
          0%, 100% {
            transform: translate(0px, 0px) scale(1);
          }
          33% {
            transform: translate(30px, -50px) scale(1.1);
          }
          66% {
            transform: translate(-20px, 20px) scale(0.9);
          }
        }

        .animate-blob {
          animation: blob 8s infinite;
        }

        .animation-delay-2000 {
          animation-delay: 2s;
        }

        .animation-delay-4000 {
          animation-delay: 4s;
        }

        @keyframes fadeIn {
          0% { opacity: 0; transform: translateY(-20px); }
          100% { opacity: 1; transform: translateY(0); }
        }

        .animate-fadeIn {
          animation: fadeIn 1.5s ease-out forwards;
        }

        @keyframes slideIn {
          0% { opacity: 0; transform: translateY(40px); }
          100% { opacity: 1; transform: translateY(0); }
        }

        .animate-slideIn {
          animation: slideIn 1.2s ease-out forwards;
        }

        /* Responsive */
        @media (max-width: 768px) {
          .btn-custom-glow {
            font-size: 1rem;
            padding: 10px 20px;
          }
        }
      `}</style>
    </>
  );
}
