import { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext'; // Import cart context

export default function Home() {
  const [arts, setArts] = useState([]);
  const { addToCart } = useCart(); // Destructure addToCart
  const navigate = useNavigate(); // For redirecting

  useEffect(() => {
    const fetchArts = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/artworks');
        console.log('Artworks:', res.data); // Debug: Check imageUrl values
        setArts(res.data);
      } catch (err) {
        console.error('Failed to load artworks', err);
      }
    };
    fetchArts();
  }, []);

  const handleBuyNow = (art) => {
    addToCart(art);           // Add the artwork to cart
    navigate('/cart');        // Navigate to cart page
  };

  return (
    <div className="max-w-6xl mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-6">Gallery</h1>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
        {arts.map((art) => (
          <div key={art._id} className="border p-4 rounded shadow">
            <img
              src={art.imageUrl ? `http://localhost:5000/${art.imageUrl}` : 'https://via.placeholder.com/300'}
              alt={art.title}
              className="w-full h-48 object-cover rounded mb-2"
              onError={(e) => {
                e.target.onerror = null;
                e.target.src = 'https://via.placeholder.com/300';
              }}
            />
            <h2 className="text-xl font-bold">{art.title}</h2>
            <p className="text-gray-600">â‚¹{art.price}</p>
            <p className="text-sm">{art.description}</p>
            <button
              onClick={() => handleBuyNow(art)}
              className="mt-2 bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
            >
              Buy Now
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}
