import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';

export default function Gallery() {
  const [artworks, setArtworks] = useState([]);
  const [search, setSearch] = useState('');
  const [minPrice, setMinPrice] = useState('');
  const [maxPrice, setMaxPrice] = useState('');
  const navigate = useNavigate();

  const { addToCart } = useCart();

  const fetchArtworks = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/artworks', {
        params: { search, minPrice, maxPrice },
      });
      setArtworks(res.data);
    } catch (err) {
      console.error('Error loading artworks:', err);
    }
  };

  useEffect(() => {
    fetchArtworks();
  }, []);

  const handleFilter = (e) => {
    e.preventDefault();
    fetchArtworks();
  };

  const handleBuyNow = (art) => {
    addToCart(art);
    navigate('/cart');
  };

  return (
    <div className="gallery-container">
      <h1 className="gallery-header">🖼️ Explore the Collection</h1>

      <form onSubmit={handleFilter} className="filter-form">
        <input
          type="text"
          placeholder="Search title"
          className="filter-input"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
        <input
          type="number"
          placeholder="Min ₹"
          className="filter-input"
          value={minPrice}
          onChange={(e) => setMinPrice(e.target.value)}
        />
        <input
          type="number"
          placeholder="Max ₹"
          className="filter-input"
          value={maxPrice}
          onChange={(e) => setMaxPrice(e.target.value)}
        />
        <button type="submit" className="filter-button">🎯 Filter</button>
      </form>

      <div className="artwork-grid">
        {artworks.map((art) => (
          <div key={art._id} className="artwork-card">
            <img
              src={`http://localhost:5000${art.imageUrl}`}
              alt={art.title}
              className="artwork-image"
            />
            <Link to={`/art/${art._id}`}>
              <h2 className="artwork-title">{art.title}</h2>
            </Link>

            <div className="artwork-meta">
              <span className="price-tag">₹{art.price}</span>
            </div>

            <p className="artwork-description">{art.description}</p>

            <button onClick={() => handleBuyNow(art)} className="buy-button">
              🛒 Buy Now
            </button>

            <Link to={`/edit/${art._id}`} className="edit-button">
              ✏️ Edit
            </Link>
          </div>
        ))}
      </div>

      <style>{GalleryStyles}</style>
    </div>
  );
}

const GalleryStyles = `
  body {
    background: linear-gradient(to right, #f8f9fa, #eef1f5);
    font-family: 'Poppins', sans-serif;
  }

  .gallery-container {
    padding: 2rem;
    max-width: 1300px;
    margin: auto;
  }

  .gallery-header {
    font-size: 3rem;
    text-align: center;
    margin-bottom: 2.5rem;
    color: #1d3557;
  }

  .filter-form {
    display: flex;
    justify-content: center;
    flex-wrap: wrap;
    gap: 12px;
    margin-bottom: 2.5rem;
  }

  .filter-input {
    padding: 12px;
    border-radius: 12px;
    border: 1px solid #ccc;
    width: 180px;
    font-size: 1rem;
    background: white;
    transition: border 0.3s ease;
  }

  .filter-input:focus {
    outline: none;
    border: 1px solid #00b894;
  }

  .filter-button {
    padding: 12px 24px;
    background: linear-gradient(to right, #00b894, #00cec9);
    border: none;
    color: white;
    border-radius: 12px;
    font-weight: bold;
    cursor: pointer;
    transition: background 0.3s ease;
  }

  .filter-button:hover {
    background: linear-gradient(to right, #00cec9, #00b894);
  }

  .artwork-grid {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(270px, 1fr));
    gap: 30px;
  }

  .artwork-card {
    position: relative;
    background: rgba(255, 255, 255, 0.85);
    border-radius: 20px;
    box-shadow: 0 8px 24px rgba(0,0,0,0.1);
    overflow: hidden;
    transition: transform 0.3s ease, box-shadow 0.3s ease;
    backdrop-filter: blur(8px);
  }

  .artwork-card:hover {
    transform: translateY(-6px);
    box-shadow: 0 16px 28px rgba(0,0,0,0.15);
  }

  .artwork-image {
    width: 100%;
    height: 200px;
    object-fit: cover;
    transition: transform 0.3s ease;
  }

  .artwork-card:hover .artwork-image {
    transform: scale(1.05);
  }

  .artwork-title {
    font-size: 1.4rem;
    font-weight: 600;
    padding: 1rem 1rem 0.5rem;
    color: #2d3436;
  }

  .artwork-meta {
    padding: 0 1rem;
    display: flex;
    justify-content: flex-start;
    align-items: center;
  }

  .price-tag {
    background-color: #ffeaa7;
    color: #d35400;
    font-weight: bold;
    padding: 6px 12px;
    border-radius: 30px;
    font-size: 1rem;
    margin: 0.3rem 0;
  }

  .artwork-description {
    font-size: 0.95rem;
    color: #555;
    padding: 0 1rem 1.5rem;
  }

  .buy-button {
    position: absolute;
    bottom: 16px;
    left: 50%;
    transform: translateX(-50%);
    padding: 10px 26px;
    background: linear-gradient(to right, #6c5ce7, #a29bfe);
    color: white;
    border: none;
    border-radius: 30px;
    font-weight: 600;
    cursor: pointer;
    transition: background 0.3s ease;
  }

  .buy-button:hover {
    background: linear-gradient(to right, #a29bfe, #6c5ce7);
  }

  .edit-button {
    position: absolute;
    top: 12px;
    right: 12px;
    background-color: #0984e3;
    color: white;
    padding: 6px 12px;
    border-radius: 10px;
    text-decoration: none;
    font-size: 0.85rem;
    transition: background-color 0.3s ease;
  }

  .edit-button:hover {
    background-color: #74b9ff;
  }
`;
