import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function Orders() {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/orders');
        setOrders(res.data);
      } catch (err) {
        console.error('Error fetching orders:', err);
      }
    };

    fetchOrders();
  }, []);

  return (
    <div className="gallery-container">
      <style>{OrderStyles}</style>
      <h1 className="gallery-header">ðŸ§¾ My Orders</h1>

      <div className="artwork-grid">
        {orders.map((order) => (
          <div key={order._id} className="artwork-card">
            <div className="image-wrapper">
              <img
                src={`http://localhost:5000${order.artwork.imageUrl}`}
                alt={order.artwork.title}
                className="artwork-image"
              />
            </div>

            <div className="price-tag">â‚¹{order.total}</div>

            <h2 className="artwork-title">{order.artwork.title}</h2>
            <p className="artwork-description">Status: {order.status}</p>
            <p className="artwork-description">Ordered on: {new Date(order.createdAt).toLocaleDateString()}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

const OrderStyles = `
  body {
    font-family: 'Segoe UI', sans-serif;
    background-color: black;
  }

  .gallery-container {
    padding: 2rem;
    max-width: 1400px;
    margin: auto;
    animation: fadeIn 1s ease;
  }

  .gallery-header {
    font-size: 2.8rem;
    text-align: center;
    font-weight: 800;
    margin-bottom: 2rem;
    color: #dfe6e9;
  }

  .artwork-grid {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(260px, 1fr));
    gap: 32px;
  }

  .artwork-card {
    background: rgba(255, 255, 255, 0.15);
    backdrop-filter: blur(20px);
    border-radius: 20px;
    padding: 1rem;
    position: relative;
    box-shadow: 0 10px 25px rgba(0,0,0,0.1);
    border: 1px solid rgba(255, 255, 255, 0.25);
    transform: translateY(20px);
    animation: fadeUp 0.6s forwards;
    transition: all 0.4s ease;
  }

  .artwork-card:hover {
    transform: translateY(-5px);
    box-shadow: 0 20px 60px rgba(0,0,0,0.15);
    border: 2px solid #a29bfe;
  }

  .image-wrapper {
    overflow: hidden;
    border-radius: 15px;
    border: 2px solid transparent;
    transition: all 0.4s ease;
  }

  .artwork-card:hover .image-wrapper {
    border-image: linear-gradient(45deg, #81ecec, #74b9ff, #a29bfe) 1;
  }

  .artwork-image {
    width: 100%;
    height: 220px;
    object-fit: cover;
    border-radius: 10px;
    transition: transform 0.4s ease;
  }

  .artwork-card:hover .artwork-image {
    transform: scale(1.05);
  }

  .price-tag {
    position: absolute;
    top: 1rem;
    left: 1rem;
    background: linear-gradient(to right, #f0932b, #eb4d4b);
    padding: 6px 14px;
    border-radius: 50px;
    font-weight: bold;
    color: white;
    font-size: 0.95rem;
    box-shadow: 0 0 6px rgba(0,0,0,0.2);
  }

  .artwork-title {
    font-size: 1.4rem;
    margin: 1rem 0 0.5rem;
    color:rgb(164, 175, 178);
    font-weight: 700;
  }

  .artwork-description {
    font-size: 0.95rem;
    color: #dfe6e9;
    margin-bottom: 1.5rem;
  }

  @keyframes fadeUp {
    to {
      transform: translateY(0);
      opacity: 1;
    }
    from {
      transform: translateY(20px);
      opacity: 0;
    }
  }

  @keyframes fadeIn {
    0% { opacity: 0; transform: scale(0.95); }
    100% { opacity: 1; transform: scale(1); }
  }
`;
