import { Link } from 'react-router-dom';

export default function ArtCard({ art }) {
  return (
    <div className="border rounded-lg p-4 shadow hover:shadow-lg transition duration-300">
      <img
        src={`http://localhost:5000${art.image}`}
        alt={art.title || 'Artwork'}
        className="w-full h-48 object-cover rounded mb-4"
      />
      <h3 className="text-lg font-semibold mb-1">{art.title}</h3>
      <p className="text-sm text-gray-600 mb-2">By User: {art.userId}</p>
      <Link
        to={`/art/${art._id}`}
        className="inline-block bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition"
      >
        View
      </Link>
    </div>
  );
}
