import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function MyOrders() {
  const [orders, setOrders] = useState([]);
  const [isLoggedIn, setIsLoggedIn] = useState(true);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      setIsLoggedIn(false);
      setLoading(false);
      return;
    }

    const fetchOrders = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/orders/my-orders', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setOrders(res.data);
      } catch (err) {
        console.error('Failed to fetch orders:', err);
        setIsLoggedIn(false);
      } finally {
        setLoading(false);
      }
    };

    fetchOrders();
  }, []);

  const handleDelete = async (orderId) => {
    const token = localStorage.getItem('token');
    if (!token) return;
    if (!window.confirm('Are you sure you want to delete this order?')) return;

    try {
      await axios.delete(`http://localhost:5000/api/orders/${orderId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      setOrders((prev) => prev.filter((o) => o._id !== orderId));
    } catch (err) {
      console.error('Failed to delete order:', err);
      alert('Could not delete the order.');
    }
  };

  if (loading) return <div className="text-center p-8 text-cyan-400 text-xl">üåü Loading your masterpieces...</div>;

  if (!isLoggedIn) {
    return (
      <div className="p-8 text-center">
        <h2 className="text-2xl font-bold text-red-600">üö´ Please login to view your orders.</h2>
      </div>
    );
  }

  if (orders.length === 0) {
    return (
      <div className="p-8 text-center">
        <h2 className="text-2xl font-bold text-gray-300">üñºÔ∏è No orders yet. Start collecting art!</h2>
      </div>
    );
  }

  return (
    <div className="p-6 max-w-3xl mx-auto text-white relative z-10">
      <h2 className="text-4xl font-extrabold text-cyan-400 mb-8 drop-shadow-glow text-center">üßæ My Orders</h2>

      {orders.map((order) => (
        <div
          key={order._id}
          className="order-card glass-effect mb-10 p-4 transition-transform duration-300 hover:scale-[1.01]"
        >
          <div className="flex flex-col md:flex-row justify-between items-center mb-4 gap-3">
            <p className="text-sm text-gray-300">
              üóìÔ∏è Placed on:{' '}
              <span className="text-cyan-300 font-medium">
                {new Date(order.placedAt || order.createdAt).toLocaleDateString('en-IN', {
                  year: 'numeric',
                  month: 'short',
                  day: 'numeric',
                })}
              </span>
            </p>
            <button
              onClick={() => handleDelete(order._id)}
              className="px-3 py-1.5 bg-red-600 text-white rounded-full shadow-lg hover:bg-red-700 transition-all duration-300"
            >
              ‚ùå Delete Order
            </button>
          </div>

          <div className="grid grid-cols-1 sm:grid-cols-2 gap-6">
            {order.items.map((item, index) => (
              <div
                key={index}
                className="art-card-small relative rounded-xl overflow-hidden shadow-md bg-gray-500/50 p-2 border border-cyan-500/30 hover:shadow-cyan-500/50 transition-all duration-300 hover:-translate-y-1 backdrop-blur-sm"
              >
                <div className="shine"></div>
                <img
                  src={`http://localhost:5000${item.imageUrl}`}
                  alt={item.title}
                  className="w-full h-32 object-cover rounded-md mb-2 transition-transform duration-300 hover:scale-110"
                />
                <h3 className="text-xs font-semibold text-cyan-300">{item.title}</h3>
                <p className="text-xs text-gray-200">üí∞ ‚Çπ{Number(item.price).toFixed(2)}</p>
              </div>
            ))}
          </div>
        </div>
      ))}

      {/* Extra Styling */}
      <style>{`
        body {
          font-family: 'Segoe UI', sans-serif;
          background-color: black;
        }

        .glass-effect {
          background: rgba(255, 255, 255, 0.05);
          backdrop-filter: blur(12px);
          border-radius: 20px;
          border: 1px solid rgba(255, 255, 255, 0.1);
          box-shadow: 0 10px 25px rgba(0,0,0,0.3);
        }

        .shine {
          position: absolute;
          top: 0;
          left: -75%;
          width: 50%;
          height: 100%;
          background: linear-gradient(to right, rgba(255,255,255,0), rgba(255,255,255,0.2), rgba(255,255,255,0));
          transform: skewX(-25deg);
          animation: shine 2s infinite;
        }

        @keyframes shine {
          0% {
            left: -75%;
          }
          100% {
            left: 125%;
          }
        }

        .drop-shadow-glow {
          text-shadow: 0 0 10px rgba(0, 255, 255, 0.7), 0 0 20px rgba(0, 255, 255, 0.5);
        }
      `}</style>
    </div>
  );
}
