// src/pages/AdminDashboard.js
import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import {
  FaImages, FaUsers, FaShoppingCart, FaChartLine,
  FaTrash, FaEdit, FaBell
} from 'react-icons/fa';
import {
  LineChart, Line, XAxis, YAxis, Tooltip, ResponsiveContainer
} from 'recharts';

export default function AdminDashboard() {
  const [artworks, setArtworks] = useState([]);
  const [filteredArtworks, setFilteredArtworks] = useState([]);
  const [search, setSearch] = useState('');
  const [recentOrders, setRecentOrders] = useState([]);
  const [notifications, setNotifications] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/admin');
      return;
    }
    fetchArtworks(token);
    fetchOrders(token);
    fetchNotifications();
  }, [navigate]);

  const fetchArtworks = async (token) => {
    try {
      const res = await axios.get('http://localhost:5000/api/artworks', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setArtworks(res.data);
      setFilteredArtworks(res.data);
    } catch (err) {
      console.error(err);
      navigate('/login');
    }
  };

  const fetchOrders = async (token) => {
    try {
      const res = await axios.get('http://localhost:5000/api/orders/recent', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setRecentOrders(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const fetchNotifications = () => {
    // Simulated notifications (replace with real backend call if needed)
    setNotifications([
      "🎨 New artwork 'Mystic Forest' added.",
      "🛒 Order #12345 placed by Aditi (₹1800).",
      "⚠️ 'Sunset Bliss' stock is low.",
    ]);
  };

  const handleDelete = async (id) => {
    const token = localStorage.getItem('token');
    if (!window.confirm('Are you sure you want to delete this artwork?')) return;
    try {
      await axios.delete(`http://localhost:5000/api/artworks/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setArtworks(prev => prev.filter(a => a._id !== id));
      setFilteredArtworks(prev => prev.filter(a => a._id !== id));
    } catch (err) {
      alert('Failed to delete artwork');
      console.error(err);
    }
  };

  const handleEdit = (id) => {
    navigate(`/admin/edit-artwork/${id}`);
  };

  const toggleStockStatus = async (id, inStock) => {
    const token = localStorage.getItem('token');
    try {
      await axios.put(
        `http://localhost:5000/api/artworks/${id}/stock`,
        { inStock },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      fetchArtworks(token);
    } catch (err) {
      console.error(err);
    }
  };

  const handleSearch = (e) => {
    const keyword = e.target.value.toLowerCase();
    setSearch(keyword);
    setFilteredArtworks(
      artworks.filter((art) =>
        art.title.toLowerCase().includes(keyword)
      )
    );
  };

  const chartData = [
    { date: 'Apr 1', revenue: 1000 },
    { date: 'Apr 2', revenue: 1200 },
    { date: 'Apr 3', revenue: 800 },
    { date: 'Apr 4', revenue: 1500 },
  ];

  return (
    <div className="flex min-h-screen bg-gray-50">
      {/* Sidebar */}
      <aside className="w-64 bg-white shadow-md p-6">
        <h1 className="text-2xl font-bold text-indigo-600 mb-6">🎨 Art Admin</h1>
        <nav className="space-y-4 text-gray-700">
          <div className="flex items-center gap-3 hover:text-indigo-600 cursor-pointer">
            <FaImages /> Artworks
          </div>
          <div className="flex items-center gap-3 hover:text-indigo-600 cursor-pointer">
            <FaUsers /> Users
          </div>
          <div className="flex items-center gap-3 hover:text-indigo-600 cursor-pointer">
            <FaShoppingCart /> Orders
          </div>
          <div className="flex items-center gap-3 hover:text-indigo-600 cursor-pointer">
            <FaChartLine /> Analytics
          </div>
          <div className="flex items-center gap-3 hover:text-red-600 cursor-pointer mt-10" onClick={() => {
            localStorage.removeItem('token');
            navigate('/admin');
          }}>
            🚪 Logout
          </div>
        </nav>
      </aside>

      {/* Main Content */}
      <main className="flex-1 p-8">
        {/* Stats */}
        <h2 className="text-3xl font-bold text-gray-800">Dashboard Overview</h2>
        <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mt-6">
          <StatCard title="Total Artworks" value={artworks.length} icon={<FaImages />} />
          <StatCard title="Total Orders" value={recentOrders.length || "42"} icon={<FaShoppingCart />} />
          <StatCard title="Total Users" value="128" icon={<FaUsers />} />
          <StatCard title="Revenue" value="₹12,500" icon={<FaChartLine />} />
        </div>

        {/* Revenue Chart */}
        <div className="mt-10">
          <h3 className="text-2xl font-semibold mb-4">Revenue Trends</h3>
          <ResponsiveContainer width="100%" height={300}>
            <LineChart data={chartData}>
              <XAxis dataKey="date" />
              <YAxis />
              <Tooltip />
              <Line type="monotone" dataKey="revenue" stroke="#4f46e5" strokeWidth={2} />
            </LineChart>
          </ResponsiveContainer>
        </div>

        {/* Notifications */}
        <div className="mt-10">
          <h3 className="text-2xl font-semibold mb-4 flex items-center gap-2"><FaBell />Notifications</h3>
          <ul className="space-y-2 text-sm text-gray-700">
            {notifications.map((note, i) => (
              <li key={i}>• {note}</li>
            ))}
          </ul>
        </div>

        {/* Recent Orders */}
        <div className="mt-10">
          <h3 className="text-2xl font-semibold mb-4">Recent Orders</h3>
          <div className="bg-white rounded-lg shadow p-4 overflow-x-auto">
            <table className="min-w-full text-sm">
              <thead>
                <tr className="text-left border-b">
                  <th className="p-2">Order ID</th>
                  <th className="p-2">User</th>
                  <th className="p-2">Amount</th>
                  <th className="p-2">Status</th>
                </tr>
              </thead>
              <tbody>
                {recentOrders.map(order => (
                  <tr key={order._id}>
                    <td className="p-2">{order._id}</td>
                    <td className="p-2">{order.userName}</td>
                    <td className="p-2">₹{order.totalAmount}</td>
                    <td className="p-2 text-green-600 font-medium">{order.status}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>

        {/* Artworks List */}
        <div className="flex justify-between items-center my-6">
          <h3 className="text-2xl font-semibold">All Artworks</h3>
          <input
            type="text"
            value={search}
            onChange={handleSearch}
            placeholder="Search artworks..."
            className="p-2 border rounded w-64"
          />
          <button
            onClick={() => navigate('/admin/upload')}
            className="bg-indigo-600 hover:bg-indigo-700 text-white font-semibold px-4 py-2 rounded"
          >
            + Add New
          </button>
        </div>

        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {filteredArtworks.map((art) => (
            <div key={art._id} className="bg-white rounded-xl shadow hover:shadow-lg transition">
              <img
                src={art.imageUrl}
                alt={art.title}
                className="w-full h-56 object-cover rounded-t-xl"
              />
              <div className="p-4">
                <h4 className="text-lg font-semibold text-gray-800">{art.title}</h4>
                <p className="text-sm text-gray-500">{art.category || 'Uncategorized'}</p>
                <div className="flex justify-between items-center mt-2">
                  <span className="text-gray-800 font-bold">₹{art.price}</span>
                  <button
                    className={`text-xs px-2 py-1 rounded ${
                      art.inStock ? 'bg-green-200 text-green-800' : 'bg-red-200 text-red-800'
                    }`}
                    onClick={() => toggleStockStatus(art._id, !art.inStock)}
                  >
                    {art.inStock ? 'In Stock' : 'Out of Stock'}
                  </button>
                </div>
                <div className="flex gap-2 mt-4">
                  <button
                    onClick={() => handleEdit(art._id)}
                    className="flex-1 flex items-center justify-center bg-yellow-400 hover:bg-yellow-500 text-white rounded py-1"
                  >
                    <FaEdit className="mr-1" /> Edit
                  </button>
                  <button
                    onClick={() => handleDelete(art._id)}
                    className="flex-1 flex items-center justify-center bg-red-500 hover:bg-red-600 text-white rounded py-1"
                  >
                    <FaTrash className="mr-1" /> Delete
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>

        {filteredArtworks.length === 0 && (
          <p className="text-gray-600 text-center mt-10">No artworks available.</p>
        )}
      </main>
    </div>
  );
}

function StatCard({ title, value, icon }) {
  return (
    <div className="bg-white rounded-xl shadow p-4 flex items-center gap-4">
      <div className="text-3xl text-indigo-600">{icon}</div>
      <div>
        <p className="text-gray-500 text-sm">{title}</p>
        <h4 className="text-xl font-bold text-gray-800">{value}</h4>
      </div>
    </div>
  );
}
