// src/pages/EditArtwork.js
import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

export default function EditArtwork() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [art, setArt] = useState(null);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [price, setPrice] = useState('');
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const style = document.createElement('style');
    style.innerHTML = `
  @import url('https://fonts.googleapis.com/css2?family=Orbitron:wght@500&display=swap');

  body {
    margin: 0;
    font-family: 'Orbitron', sans-serif;
    background: #0f0f0f;
  }

  .upload-wrapper {
    min-height: 82vh;
    display: flex;
    align-items: center;
    justify-content: center;
    padding: 2rem;
  }

  .upload-container {
    background: #1a1a1a;
    border-radius: 16px;
    padding: 40px;
    box-shadow: 0 0 20px rgba(0, 255, 255, 0.2);
    width: 100%;
    max-width: 500px;
    border: 2px solid #00e0ff33;
    transition: 0.3s ease;
    min-height: 70vh;
    margin-top: -1%;
  }

  .upload-container:hover {
    box-shadow: 0 0 40px rgba(0, 255, 255, 0.4);
  }

  .upload-header {
    font-size: 2rem;
    font-weight: bold;
    text-align: center;
    margin-bottom: 30px;
    color: #00ffe5;
    text-shadow: 0 0 6px #00fff7, 0 0 10px #00fff7;
  }

  .upload-form .form-group {
    margin-bottom: 20px;
  }

  .upload-form label {
    display: block;
    color: #ccc;
    margin-bottom: 6px;
    font-weight: 600;
  }

  .upload-form input,
  .upload-form textarea {
    width: 100%;
    padding: 12px;
    background: #111;
    border: 1px solid #333;
    color: #fff;
    border-radius: 10px;
    transition: 0.3s;
  }

  .upload-form input:focus,
  .upload-form textarea:focus {
    border-color: #00ffe5;
    outline: none;
    box-shadow: 0 0 10px #00ffe533;
  }

  .image-preview {
    margin-top: 20px;
    text-align: center;
  }

  .image-preview img {
    max-width: 100%;
    border-radius: 12px;
    box-shadow: 0 0 20px rgba(0, 255, 255, 0.2);
    transition: transform 0.3s ease;
  }

  .image-preview img:hover {
    transform: scale(1.03);
  }

  .neon-btn {
    width: 100%;
    padding: 12px 0;
    font-size: 1.2rem;
    font-weight: bold;
    background: #00ffe5;
    color: #000;
    border: none;
    border-radius: 12px;
    box-shadow: 0 0 10px #00ffe5, 0 0 20px #00ffe5;
    cursor: pointer;
    transition: all 0.3s ease;
    margin-top: 2%;
  }

  .neon-btn:hover {
    background: #00b5a1;
    box-shadow: 0 0 20px #00e0ff, 0 0 40px #00e0ff;
  }
`;
    document.head.appendChild(style);

    return () => {
      document.head.removeChild(style);
    };
  }, []);

  useEffect(() => {
    const fetchArtwork = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/artworks/${id}`);
        setArt(res.data);
        setTitle(res.data.title || '');
        setDescription(res.data.description || '');
        setPrice(res.data.price || '');
      } catch (err) {
        console.error('Error fetching artwork:', err);
        alert('Failed to load artwork.');
        navigate('/');
      }
    };
    fetchArtwork();
  }, [id, navigate]);

  const handleUpdate = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (!token) return alert('You must be logged in to update artwork.');

    setLoading(true);
    try {
      await axios.put(
        `http://localhost:5000/api/artworks/${id}`,
        { title, description, price },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      alert('Artwork updated successfully!');
      navigate('/artgallery');
    } catch (err) {
      console.error('Update error:', err);
      alert('Failed to update artwork.');
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async () => {
    if (!window.confirm('Are you sure you want to delete this artwork?')) return;
    const token = localStorage.getItem('token');
    if (!token) return alert('You must be logged in to delete artwork.');

    try {
      await axios.delete(`http://localhost:5000/api/artworks/${art._id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert('Artwork deleted!');
      navigate('/artgallery');
    } catch (err) {
      console.error('Delete error:', err);
      alert('Failed to delete artwork.');
    }
  };

  if (!art) {
    return (
      <div className="loading-container">
        Loading artwork...
      </div>
    );
  }

  return (
    <div className="edit-artwork-container">
      <h2 className="edit-artwork-heading">
        🎨 Edit Artwork
      </h2>

      <form onSubmit={handleUpdate}>
        <div className="form-group">
          <label>Title</label>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Enter artwork title"
            required
          />
        </div>

        <div className="form-group">
          <label>Description</label>
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            rows={4}
            placeholder="Enter description"
            required
          />
        </div>

        <div className="form-group">
          <label>Price (₹)</label>
          <input
            type="number"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
            placeholder="Enter price"
            required
          />
        </div>

        <button
          type="submit"
          disabled={loading}
          className="submit-button"
        >
          {loading ? 'Updating...' : '💾 Update Artwork'}
        </button>
      </form>

      <div className="text-center">
        <button
          onClick={handleDelete}
          className="delete-button"
        >
          🗑️ Delete Artwork
        </button>
      </div>
    </div>
  );
}
