import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';
import { useNavigate } from 'react-router-dom';

export default function Home() {
  const navigate = useNavigate();

  const handleNavigate = (role) => {
    if (role === 'Admin') navigate('/admin-dashboard');
    else if (role === 'Artist') navigate('/artist-dashboard');
    else if (role === 'Customer') navigate('/customer-dashboard');
  };

  return (
    <>
      <div className="max-w-6xl mx-auto px-4 py-8">
        <h1 className="text-3xl font-bold mb-6 text-center text-blue-900">
          Welcome to Yuvi's Artistry
        </h1>

        <Carousel
          autoPlay
          infiniteLoop
          showThumbs={false}
          showStatus={false}
          className="mb-10"
        >
          {[1, 2, 3, 4, 5].map((num) => (
            <div key={num}>
              <img
                src={`/images/art${num}.jpg`}
                alt={`Art ${num}`}
                className="h-[100px] w-90% object-cover rounded-xl shadow-lg"
              />
              <p className="legend text-lg bg-opacity-70 bg-black text-white p-2">
                Art Title {num} - â‚¹{num * 500 + 499}
              </p>
            </div>
          ))}
        </Carousel>

        {/* ðŸ”‘ Cards Section */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mt-12">
          {[
            {
              role: 'Admin',
              description: 'Manage all users, artworks, and orders across the platform.',
              icon: 'ðŸ› ï¸',
              color: 'from-purple-500 to-indigo-600',
            },
            {
              role: 'Artist',
              description: 'Upload your masterpieces and manage your art sales.',
              icon: 'ðŸŽ¨',
              color: 'from-pink-500 to-red-500',
            },
            {
              role: 'Customer',
              description: 'Browse, buy, and enjoy beautiful artworks.',
              icon: 'ðŸ›’',
              color: 'from-green-500 to-teal-500',
            },
          ].map(({ role, description, icon, color }) => (
            <div
              key={role}
              className={`bg-gradient-to-br ${color} text-white rounded-xl p-6 text-center shadow-lg hover:shadow-2xl transform transition-transform hover:-translate-y-2`}
            >
              <div className="text-5xl mb-4">{icon}</div>
              <h3 className="text-2xl font-bold mb-2">{role}</h3>
              <p className="mb-4">{description}</p>
              <button
                onClick={() => handleNavigate(role)}
                className="bg-white text-gray-800 font-semibold py-2 px-4 rounded-full shadow-md hover:bg-gray-100 transition duration-300"
              >
                Go to {role}
              </button>
            </div>
          ))}
        </div>
      </div>

      <style>{`
        h1 {
          text-align: center;
          text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.3);
        }

        .carousel .slide {
          background-color: #f3f4f6;
          width: 90%;
          height: 76vh;
        }

        .carousel .legend {
          font-size: 1.25rem;
          font-weight: bold;
          text-align: center;
          background: rgba(0, 0, 0, 0.7);
          color: white;
          padding: 10px;
          border-radius: 5px;
          margin-top: 5%;
        }

        .grid img {
          transition: transform 0.3s ease;
          object-fit: cover;
          height: 90%;
        }

        .grid img:hover {
          transform: scale(1.05);
        }
      `}</style>
    </>
  );
}
