import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function MyOrders() {
  const [orders, setOrders] = useState([]);
  const [isLoggedIn, setIsLoggedIn] = useState(true);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      setIsLoggedIn(false);
      setLoading(false);
      return;
    }

    const fetchOrders = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/orders/my-orders', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setOrders(res.data);
      } catch (err) {
        console.error('Failed to fetch orders:', err);
        setIsLoggedIn(false);
      } finally {
        setLoading(false);
      }
    };

    fetchOrders();
  }, []);

  if (loading) return <div className="text-center p-8 text-cyan-400 text-xl">🌟 Loading your masterpieces...</div>;

  if (!isLoggedIn) {
    return (
      <div className="p-8 text-center">
        <h2 className="text-2xl font-bold text-red-600">🚫 Please login to view your orders.</h2>
      </div>
    );
  }

  if (orders.length === 0) {
    return (
      <div className="p-8 text-center">
        <h2 className="text-2xl font-bold text-gray-300">🖼️ No orders yet. Start collecting art!</h2>
      </div>
    );
  }

  return (
    <div className="p-8 max-w-6xl mx-auto text-white relative z-10">
      <h2 className="text-4xl font-extrabold text-cyan-400 mb-8 drop-shadow-glow">🧾 My Orders</h2>

      {orders.map((order) => (
        <div
          key={order._id}
          className="order-card glass-effect mb-10 p-4 transition-transform duration-100 hover:scale-[1.01]"
        >
          <div className="flex flex-col md:flex-row justify-between items-center mb-4 gap-3">
            <p className="text-sm text-gray-300">
              🗓️ Placed on:{' '}
              <span className="text-cyan-300 font-medium">
                {new Date(order.placedAt || order.createdAt).toLocaleDateString('en-IN', {
                  year: 'numeric',
                  month: 'short',
                  day: 'numeric',
                })}
              </span>
            </p>
            <button
              onClick={() => handleDelete(order._id)}
              className="px-3 py-1.5 bg-red-600 text-white rounded-full shadow-lg hover:bg-red-700 transition-all duration-300"
            >
              ❌ Delete Order
            </button>
          </div>

          <div className="grid grid-cols-1 sm:grid-cols-2 gap-6">
            {order.items.map((item, index) => (
              <div
                key={index}
                className="art-card-small relative rounded-xl overflow-hidden shadow-md bg-gray-800/50 p-2 border border-cyan-100/30 hover:shadow-cyan-500/50 transition-all duration-300 hover:-translate-y-1 backdrop-blur-sm"
              >
                <div className="shine"></div>
                <img
                  src={`http://localhost:5000${item.imageUrl}`}
                  alt={item.title}
                  className=" rounded-md mb-2 transition-transform duration-300 hover:scale-110"
                width="200px" height="200px"/>
                <h3 className="text-xs font-semibold text-cyan-300">{item.title}</h3>
                <p className="text-xs text-gray-200">💰 ₹{Number(item.price).toFixed(2)}</p>
              </div>
            ))}
          </div>
        </div>
      ))}

      <style>{`
        body {
    font-family: 'Segoe UI', sans-serif;
    background-color: black;
  }

  .order-container {
    padding: 2rem;
    max-width: 1400px;
    margin: auto;
    animation: fadeIn 1s ease;
  }

  .order-header {
    font-size: 2.8rem;
    text-align: center;
    font-weight: 800;
    margin-bottom: 2rem;
    color: #2d3436;
  }

  .order-grid {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(260px, 1fr));
    gap: 32px;
  }

  .order-card {
    background: rgba(255, 255, 255, 0.15);
    backdrop-filter: blur(20px);
    border-radius: 20px;
    padding: 1rem;
    position: relative;
    box-shadow: 0 10px 25px rgba(0,0,0,0.1);
    border: 1px solid rgba(255, 255, 255, 0.25);
    transform: translateY(20px);
    animation: fadeUp 0.6s forwards;
    transition: all 0.4s ease;
    max-width:300px;
  }

  .order-card:hover {
    transform: translateY(-5px);
    box-shadow: 0 20px 60px rgba(0,0,0,0.15);
    border: 2px solid #a29bfe;
  }

  .image-wrapper {
    overflow: hidden;
    border-radius: 15px;
    border: 2px solid transparent;
    transition: all 0.4s ease;
  }

  .order-card:hover .image-wrapper {
    border-image: linear-gradient(45deg, #81ecec, #74b9ff, #a29bfe) 1;
  }

  .artwork-image {
    width: 100px;
    height: 100px;
    object-fit: cover;
    border-radius: 10px;
    transition: transform 0.4s ease;
  }

  .order-card:hover .artwork-image {
    transform: scale(1.05);
  }

  .order-info {
    margin-top: 1rem;
  }

  .artwork-title {
    font-size: 1.4rem;
    color: rgb(164, 175, 178);
    font-weight: 700;
    margin-bottom: 0.5rem;
  }

  .order-detail {
    font-size: 0.95rem;
    color: #636e72;
    margin-bottom: 0.3rem;
  }

  @keyframes fadeUp {
    to {
      transform: translateY(0);
      opacity: 1;
    }
    from {
      transform: translateY(20px);
      opacity: 0;
    }
  }

  @keyframes fadeIn {
    0% { opacity: 0; transform: scale(0.95); }
    100% { opacity: 1; transform: scale(1); }
  
`}</style>
    </div>
  );

  async function handleDelete(orderId) {
    const token = localStorage.getItem('token');
    if (!token) return;

    if (!window.confirm('Are you sure you want to delete this order?')) return;

    try {
      await axios.delete(`http://localhost:5000/api/orders/${orderId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      setOrders((prev) => prev.filter((o) => o._id !== orderId));
    } catch (err) {
      console.error('Failed to delete order:', err);
      alert('Could not delete the order.');
    }
  }
}
