// Paste the full component I gave earlier here
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ArtGallery = () => {
  const [artworks, setArtworks] = useState([]);

  useEffect(() => {
    const fetchArtworks = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/art');
        setArtworks(res.data);
      } catch (err) {
        console.error('Error fetching artworks:', err);
      }
    };

    fetchArtworks();
  }, []);

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Explore Artworks</h2>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '20px' }}>
        {artworks.map((art) => (
          <div key={art._id} style={{
            border: '1px solid #ccc',
            borderRadius: '10px',
            padding: '1rem',
            width: '250px',
            boxShadow: '2px 2px 10px rgba(0,0,0,0.1)'
          }}>
            <img src={art.imageUrl} alt={art.title} style={{ width: '100%', height: '200px', objectFit: 'cover' }} />
            <h3>{art.title}</h3>
            <p><strong>Price:</strong> â‚¹{art.price}</p>
            <p>{art.description}</p>
            <small>By: {art.artistId?.name || 'Unknown Artist'}</small>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ArtGallery;
