import { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

export default function Upload() {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [price, setPrice] = useState('');
  const [imageFile, setImageFile] = useState(null);
  const [preview, setPreview] = useState('');
  const [loading, setLoading] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(true);

  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) setIsLoggedIn(false);
  }, []);

  const handleImageUpload = async () => {
    const formData = new FormData();
    formData.append('image', imageFile);
    const res = await axios.post('http://localhost:5000/api/upload/upload', formData);
    return res.data.imageUrl;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (!token) {
      alert('You must be logged in.');
      return;
    }

    try {
      setLoading(true);
      const imageUrl = await handleImageUpload();

      await axios.post(
        'http://localhost:5000/api/artworks',
        { title, description, price, imageUrl },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      alert('Artwork uploaded successfully!');
      navigate('/artgallery');
    } catch (err) {
      console.error(err);
      alert('Upload failed!');
    } finally {
      setLoading(false);
    }
  };

  if (!isLoggedIn) {
    return (
      <div className="upload-warning">
        ðŸš« Please login to upload your artwork.
      </div>
    );
  }

  return (
    <>
      <div className="upload-page">
        <div className="upload-card">
          <h2 className="upload-title">âœ¨ Upload Artwork</h2>
          <form onSubmit={handleSubmit} className="upload-form">
            <div className="form-group">
              <label>Title</label>
              <input value={title} onChange={(e) => setTitle(e.target.value)} required />
            </div>

            <div className="form-group">
              <label>Description</label>
              <textarea value={description} onChange={(e) => setDescription(e.target.value)} required />
            </div>

            <div className="form-group">
              <label>Price (â‚¹)</label>
              <input type="number" value={price} onChange={(e) => setPrice(e.target.value)} required />
            </div>

            <div className="form-group">
              <label>Upload Image</label>
              <input
                type="file"
                accept="image/*"
                onChange={(e) => {
                  setImageFile(e.target.files[0]);
                  setPreview(URL.createObjectURL(e.target.files[0]));
                }}
                required
              />
            </div>

            {preview && (
              <div className="image-preview">
                <img src={preview} alt="Preview" />
              </div>
            )}

            <button type="submit" disabled={loading} className="submit-btn">
              {loading ? 'Uploading...' : 'ðŸš€ Upload'}
            </button>
          </form>
        </div>
      </div>

      <style>{`
        .upload-page {
          min-height: 100vh;
          display: flex;
          justify-content: center;
          align-items: center;
          background: linear-gradient(135deg, #f0e9ff, #ffe8ec);
          padding: 30px;
        }

        .upload-card {
          background: rgba(255, 255, 255, 0.4);
          backdrop-filter: blur(16px);
          border-radius: 20px;
          padding: 40px;
          box-shadow: 0 20px 50px rgba(0, 0, 0, 0.1);
          width: 100%;
          max-width: 600px;
          transition: transform 0.3s ease, box-shadow 0.3s ease;
        }

        .upload-card:hover {
          transform: scale(1.02);
          box-shadow: 0 30px 60px rgba(0, 0, 0, 0.15);
        }

        .upload-title {
          font-size: 2.5rem;
          font-weight: bold;
          text-align: center;
          color: #6b21a8;
          margin-bottom: 30px;
          font-family: 'Segoe UI', sans-serif;
          text-shadow: 1px 1px #fff;
        }

        .upload-form .form-group {
          margin-bottom: 20px;
        }

        .upload-form label {
          display: block;
          font-weight: 600;
          margin-bottom: 6px;
          color: #333;
        }

        .upload-form input[type="text"],
        .upload-form input[type="number"],
        .upload-form textarea,
        .upload-form input[type="file"] {
          width: 100%;
          padding: 12px 14px;
          border: 1px solid #d1d5db;
          border-radius: 10px;
          background-color: #fff;
          transition: border 0.2s ease;
        }

        .upload-form input:focus,
        .upload-form textarea:focus {
          border-color: #a78bfa;
          outline: none;
        }

        .image-preview {
          text-align: center;
          margin-top: 20px;
        }

        .image-preview img {
          max-width: 100%;
          max-height: 300px;
          border-radius: 15px;
          box-shadow: 0 10px 25px rgba(0, 0, 0, 0.1);
          transition: transform 0.3s ease;
        }

        .image-preview img:hover {
          transform: scale(1.05);
        }

        .submit-btn {
          width: 100%;
          padding: 14px;
          background: linear-gradient(to right, #7c3aed, #ec4899);
          color: #fff;
          font-size: 1.1rem;
          font-weight: 600;
          border: none;
          border-radius: 12px;
          cursor: pointer;
          box-shadow: 0 8px 16px rgba(124, 58, 237, 0.3);
          transition: background 0.3s ease, transform 0.2s ease;
        }

        .submit-btn:hover {
          background: linear-gradient(to right, #6d28d9, #db2777);
          transform: scale(1.01);
        }

        .upload-warning {
          text-align: center;
          margin-top: 50px;
          padding: 20px;
          background-color: #fee2e2;
          color: #b91c1c;
          border: 1px solid #fca5a5;
          border-radius: 12px;
          max-width: 600px;
          margin-left: auto;
          margin-right: auto;
          font-weight: 600;
        }
      `}</style>
    </>
  );
}
