import { useEffect, useState } from 'react';
import axios from 'axios';

export default function ArtGallery() {
  const [artworks, setArtworks] = useState([]);

  useEffect(() => {
    const fetchArt = async () => {
      const res = await axios.get('http://localhost:5000/api/artworks');
      setArtworks(res.data);
    };
    fetchArt();
  }, []);

  return (
    <div>
      <h2>Art Gallery</h2>
      <div className="grid">
        {artworks.map((art) => (
          <div key={art._id}>
            <img src={`http://localhost:5000${art.imageUrl}`} alt={art.title} width="200" />
            <h3>{art.title}</h3>
            <p>{art.description}</p>
            <p>â‚¹{art.price}</p>
            <p>By: {art.artistId?.name}</p>
          </div>
        ))}
      </div>
    </div>
  );
}
