import { useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';

// Shared Header Component
function Header() {
  return (
    <header className="bg-black text-white py-5 shadow-md text-center">
      <h1 className="text-3xl font-bold text-cyan-400">🎨 Art Gallery</h1>
    </header>
  );
}

// Login Form Component
export function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/auth/login', {
        email,
        password,
      });
      localStorage.setItem('token', res.data.token);
      alert('Login successful!');
      navigate('/');
    } catch (err) {
      alert('Login failed: ' + (err.response?.data?.message || 'Unknown error'));
    }
  };

  return (
    <>
      <Header />
      <div className="min-h-screen bg-[#121212] text-white flex items-center justify-center px-4">
        <form onSubmit={handleLogin} className="w-full max-w-md bg-[#1e1e1e] p-8 rounded-xl shadow-2xl">
          <h2 className="text-2xl font-bold text-center text-cyan-400 mb-6">Login</h2>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Email"
            required
            className="w-full mb-4 p-3 rounded bg-[#2f2f2f] border border-gray-600 text-white focus:outline-none focus:border-cyan-400"
          />
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Password"
            required
            className="w-full mb-6 p-3 rounded bg-[#2f2f2f] border border-gray-600 text-white focus:outline-none focus:border-cyan-400"
          />
          <button
            type="submit"
            className="w-full bg-cyan-500 hover:bg-cyan-600 text-white font-semibold py-2 rounded"
          >
            Login
          </button>
          <div className="mt-5 text-center text-sm">
            <span>Don't have an account? </span>
            <Link to="/signup" className="text-cyan-400 hover:underline">Signup</Link>
          </div>
        </form>
      </div>
    </>
  );
}

// Signup Form Component
export function Signup() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSignup = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/auth/signup', { name, email, password });
      alert('Signup successful! Now log in.');
      navigate('/login');
    } catch (err) {
      alert('Signup failed: ' + (err.response?.data?.message || 'Unknown error'));
    }
  };

  return (
    <>
      <Header />
      <div className="min-h-screen bg-[#121212] text-white flex items-center justify-center px-4">
        <form onSubmit={handleSignup} className="w-full max-w-md bg-[#1e1e1e] p-8 rounded-xl shadow-2xl">
          <h2 className="text-2xl font-bold text-center text-cyan-400 mb-6">Signup</h2>
          <input
            value={name}
            onChange={(e) => setName(e.target.value)}
            placeholder="Name"
            required
            className="w-full mb-4 p-3 rounded bg-[#2f2f2f] border border-gray-600 text-white focus:outline-none focus:border-cyan-400"
          />
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Email"
            required
            className="w-full mb-4 p-3 rounded bg-[#2f2f2f] border border-gray-600 text-white focus:outline-none focus:border-cyan-400"
          />
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Password"
            required
            className="w-full mb-6 p-3 rounded bg-[#2f2f2f] border border-gray-600 text-white focus:outline-none focus:border-cyan-400"
          />
          <button
            type="submit"
            className="w-full bg-blue-600 hover:bg-blue-700 text-white font-semibold py-2 rounded"
          >
            Signup
          </button>
          <div className="mt-5 text-center text-sm">
            <span>Already have an account? </span>
            <Link to="/login" className="text-cyan-400 hover:underline">Login</Link>
          </div>
        </form>
      </div>
    </>
  );
}
