import React, { useEffect, useState } from 'react';
import axios from 'axios';

const OrderHistory = () => {
  const [orders, setOrders] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchOrders = async () => {
      const token = localStorage.getItem('token');
      if (!token) {
        setError('Please login to view your orders.');
        setLoading(false);
        return;
      }

      try {
        const res = await axios.get('http://localhost:5000/api/orders/buyer-orders', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setOrders(res.data);
      } catch (err) {
        console.error('Error fetching orders:', err);
        setError('Failed to load orders.');
      } finally {
        setLoading(false);
      }
    };

    fetchOrders();
  }, []);

  if (loading) return <p className="p-4 text-center">Loading your orders...</p>;
  if (error) return <p className="p-4 text-center text-red-500">{error}</p>;

  return (
    <div className="p-4 max-w-4xl mx-auto">
      <h2 className="text-3xl font-bold mb-6 text-center">ðŸ§¾ Your Order History</h2>

      {orders.length === 0 ? (
        <p className="text-center text-gray-600">You haven't placed any orders yet.</p>
      ) : (
        orders.map((order) => (
          <div key={order._id} className="border p-4 mb-6 rounded-lg shadow bg-white">
            <div className="mb-2">
              <h3 className="font-semibold text-lg">Order ID: <span className="text-gray-700">{order._id}</span></h3>
              <p className="text-sm text-gray-500">Placed on: {new Date(order.createdAt).toLocaleDateString()}</p>
            </div>

            <ul className="divide-y mt-2">
              {order.items.map((item) => (
                <li key={item.artworkId._id} className="py-2">
                  ðŸŽ¨ <span className="font-medium">{item.artworkId.title}</span> â€” â‚¹{item.artworkId.price}
                </li>
              ))}
            </ul>
          </div>
        ))
      )}
    </div>
  );
};

export default OrderHistory;
