import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { FaTrash } from 'react-icons/fa';

export default function AdminDashboard() {
  const [artworks, setArtworks] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/admin');
      return;
    }

    fetchArtworks(token);
  }, [navigate]);

  const fetchArtworks = async (token) => {
    try {
      const res = await axios.get('http://localhost:5000/api/artworks', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setArtworks(res.data);
    } catch (err) {
      console.error(err);
      navigate('/login'); // redirect if token invalid
    }
  };

  const handleDelete = async (id) => {
    const token = localStorage.getItem('token');
    if (!window.confirm('Are you sure you want to delete this artwork?')) return;

    try {
      await axios.delete(`http://localhost:5000/api/artworks/${id}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setArtworks(artworks.filter((art) => art._id !== id));
    } catch (err) {
      alert('Failed to delete artwork');
      console.error(err);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-gray-900 to-gray-800 text-white p-6">
      <header className="sticky top-0 z-10 bg-gray-900 bg-opacity-90 p-4 rounded shadow-lg mb-6">
        <h2 className="text-3xl font-extrabold tracking-wide text-cyan-300 drop-shadow-lg">
          ðŸŽ¨ Admin Dashboard
        </h2>
        <p className="text-sm text-gray-400 mt-1">Manage all uploaded artworks</p>
      </header>

      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
        {artworks.map((art) => (
          <div
            key={art._id}
            className="bg-gray-850 rounded-xl shadow-lg hover:shadow-cyan-500/20 transition-all duration-300 overflow-hidden group"
          >
            <img
              src={art.imageUrl}
              alt={art.title}
              className="w-full h-56 object-cover group-hover:scale-105 transition-transform duration-300"
            />
            <div className="p-4">
              <h3 className="text-xl font-semibold text-cyan-300">{art.title}</h3>
              <p className="text-sm text-gray-400 mt-1">Price: â‚¹{art.price}</p>
              <button
                onClick={() => handleDelete(art._id)}
                className="flex items-center gap-2 bg-red-600 hover:bg-red-700 mt-4 py-2 px-4 rounded-full text-white font-medium text-sm transition duration-200"
              >
                <FaTrash /> Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
