import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import { useFavorites } from '../context/FavouritesContext';
import Header from '../components/Header';

export default function Gallery() {
  const [artworks, setArtworks] = useState([]);
  const [search, setSearch] = useState('');
  const [minPrice, setMinPrice] = useState('');
  const [maxPrice, setMaxPrice] = useState('');
  const [userRole, setUserRole] = useState(null); // NEW

  const navigate = useNavigate();
  const { addToCart } = useCart();
  const { favorites, toggleFavorite } = useFavorites();

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get('http://localhost:5000/api/user/profile', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUserRole(res.data.role); // Set user role
      } catch (err) {
        console.error('Failed to fetch user:', err);
      }
    };

    fetchUser();
    fetchArtworks();
  }, []);

  const fetchArtworks = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/artworks', {
        params: { search, minPrice, maxPrice },
      });
      setArtworks(res.data);
    } catch (err) {
      console.error('Error loading artworks:', err);
    }
  };

  const handleFilter = (e) => {
    e.preventDefault();
    fetchArtworks();
  };

  const handleBuyNow = (art) => {
    addToCart(art);
    navigate('/cart');
  };

  return (
    <>
      <div className="gallery-container">
        <Header />
        <style>{GalleryStyles}</style>

        <h1 className="gallery-header">üé® Art Gallery</h1>

        <form onSubmit={handleFilter} className="filter-form">
          <input type="text" placeholder="Search by title" className="filter-input" value={search} onChange={(e) => setSearch(e.target.value)} />
          <input type="number" placeholder="Min Price" className="filter-input" value={minPrice} onChange={(e) => setMinPrice(e.target.value)} />
          <input type="number" placeholder="Max Price" className="filter-input" value={maxPrice} onChange={(e) => setMaxPrice(e.target.value)} />
          <button type="submit" className="filter-button">Filter</button>
        </form>

        <div className="artwork-grid">
          {artworks.map((art) => (
            <div key={art._id} className="artwork-card">
              <div className="image-wrapper">
                <img
                  src={`http://localhost:5000${art.imageUrl}`}
                  alt={art.title}
                  className="artwork-image"
                />
              </div>

              <div className="price-tag">‚Çπ{art.price}</div>

              <h2 className="artwork-title">{art.title}</h2>
              <p className="artwork-description">{art.description}</p>

              {userRole === 'customer' && (
                <>
                  <button
                    className={`fav-button ${favorites[art._id] ? 'active' : ''}`}
                    onClick={() => toggleFavorite(art._id)}
                  >
                    {favorites[art._id] ? '‚ù§Ô∏è' : 'ü§ç'}
                  </button>

                  <button onClick={() => handleBuyNow(art)} className="buy-button">
                    üõí Buy Now
                  </button>

                  <button className="rate-button">
                    ‚≠ê Rate
                  </button>
                </>
              )}

              {userRole === 'artist' && (
                <>
                  <button
                    onClick={() => navigate(`/edit-artwork/${art._id}`)}
                    className="edit-button"
                  >
                    ‚úèÔ∏è Edit
                  </button>

                  <div className="amount-label">
                    Amount: ‚Çπ{art.price}
                  </div>
                </>
              )}
            </div>
          ))}
        </div>
      </div>
    </>
  );
}

const GalleryStyles = `
  /* Same styles you already have */
  /* Add new buttons below */
  .edit-button {
    position: absolute;
    bottom: 1rem;
    right: 1rem;
    background: #fdcb6e;
    color: black;
    border: none;
    padding: 10px 20px;
    border-radius: 30px;
    cursor: pointer;
    font-weight: bold;
    transition: background 0.4s ease;
  }

  .edit-button:hover {
    background: #e17055;
  }

  .amount-label {
    position: absolute;
    bottom: 1rem;
    left: 1rem;
    color: white;
    font-weight: bold;
    font-size: 1rem;
  }

  .rate-button {
    position: absolute;
    bottom: 1rem;
    left: 1rem;
    background: #ffeaa7;
    color: black;
    border: none;
    padding: 8px 16px;
    border-radius: 30px;
    font-weight: bold;
    cursor: pointer;
    margin-right: 1rem;
    transition: background 0.4s ease;
  }

  .rate-button:hover {
    background: #fab1a0;
  }
`;
