// src/pages/EditArtwork.js
import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

export default function EditArtwork() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [art, setArt] = useState(null);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [price, setPrice] = useState('');
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchArtwork = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/artworks/${id}`);
        setArt(res.data);
        setTitle(res.data.title || '');
        setDescription(res.data.description || '');
        setPrice(res.data.price || '');
      } catch (err) {
        console.error('Error fetching artwork:', err);
        alert('Failed to load artwork.');
        navigate('/');
      }
    };
    fetchArtwork();
  }, [id, navigate]);

  const handleUpdate = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (!token) return alert('You must be logged in to update artwork.');

    setLoading(true);
    try {
      await axios.put(
        `http://localhost:5000/api/artworks/${id}`,
        { title, description, price },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      alert('Artwork updated successfully!');
      navigate('/artgallery');
    } catch (err) {
      console.error('Update error:', err);
      alert('Failed to update artwork.');
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async () => {
    if (!window.confirm('Are you sure you want to delete this artwork?')) return;
    const token = localStorage.getItem('token');
    if (!token) return alert('You must be logged in to delete artwork.');

    try {
      await axios.delete(`http://localhost:5000/api/artworks/${art._id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert('Artwork deleted!');
      navigate('/artgallery');
    } catch (err) {
      console.error('Delete error:', err);
      alert('Failed to delete artwork.');
    }
  };

  if (!art) {
    return (
      <div className="h-screen flex justify-center items-center text-xl font-semibold text-gray-700 animate-pulse">
        Loading artwork...
      </div>
    );
  }

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-purple-100 via-indigo-100 to-pink-100 p-6">
      <div
        className="w-full max-w-2xl rounded-3xl shadow-2xl p-10 backdrop-blur-3xl bg-white/60 border border-white/30 transition-transform transform hover:scale-[1.01]"
        style={{
          boxShadow: '0 20px 40px rgba(0, 0, 0, 0.1)',
        }}
      >
        <h2 className="text-4xl font-extrabold text-indigo-700 text-center mb-6 tracking-wide flex items-center justify-center gap-2">
          🎨 Edit Artwork
        </h2>

        <form onSubmit={handleUpdate} className="space-y-6">
          <div>
            <label className="block text-lg font-medium text-gray-700 mb-1">Title</label>
            <input
              type="text"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              placeholder="Enter artwork title"
              className="w-full px-5 py-3 border border-gray-300 rounded-xl shadow-sm focus:ring-2 focus:ring-purple-400 focus:outline-none transition"
              required
            />
          </div>

          <div>
            <label className="block text-lg font-medium text-gray-700 mb-1">Description</label>
            <textarea
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              rows={4}
              placeholder="Enter description"
              className="w-full px-5 py-3 border border-gray-300 rounded-xl shadow-sm focus:ring-2 focus:ring-purple-400 focus:outline-none transition"
              required
            />
          </div>

          <div>
            <label className="block text-lg font-medium text-gray-700 mb-1">Price (₹)</label>
            <input
              type="number"
              value={price}
              onChange={(e) => setPrice(e.target.value)}
              placeholder="Enter price"
              className="w-full px-5 py-3 border border-gray-300 rounded-xl shadow-sm focus:ring-2 focus:ring-purple-400 focus:outline-none transition"
              required
            />
          </div>

          <button
            type="submit"
            disabled={loading}
            className="w-full py-3 rounded-xl bg-gradient-to-r from-indigo-500 to-purple-500 text-white font-semibold text-lg shadow-md hover:from-purple-500 hover:to-indigo-500 transition duration-300"
          >
            {loading ? 'Updating...' : '💾 Update Artwork'}
          </button>
        </form>

        <div className="text-center mt-6">
          <button
            onClick={handleDelete}
            className="bg-red-600 hover:bg-red-700 text-white px-6 py-2 rounded-xl font-medium transition-all shadow-lg"
          >
            🗑️ Delete Artwork
          </button>
        </div>
      </div>
    </div>
  );
}
