// src/pages/CartPage.js
import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';

export default function CartPage() {
  const { cartItems, removeFromCart, clearCart } = useCart();
  const [orderMessage, setOrderMessage] = useState('');
  const navigate = useNavigate();

  const handlePlaceOrder = async () => {
    if (!cartItems || cartItems.length === 0) {
      setOrderMessage('Cart is empty or not loaded properly.');
      return;
    }

    const items = cartItems.map((item) => ({
      artwork: item._id,
    }));

    try {
      const res = await axios.post(
        'http://localhost:5000/api/orders/place',
        { items },
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        }
      );
      setOrderMessage('‚úÖ Order placed successfully!');
      clearCart();
      setTimeout(() => navigate('/my-orders'), 1500);
    } catch (err) {
      setOrderMessage(`‚ùå Order failed: ${err.response?.data?.message || 'Server error'}`);
      console.error('Order failed:', err);
    }
  };

  return (
    <div className="min-h-screen p-6 bg-gradient-to-br from-black via-gray-900 to-black text-white">
      <div className="max-w-4xl mx-auto">
        <h1 className="text-4xl font-extrabold text-center mb-8 bg-gradient-to-r from-cyan-400 to-blue-500 bg-clip-text text-transparent drop-shadow-glow">
          üõí Your Art Cart
        </h1>

        {orderMessage && (
          <p className="text-center mb-4 text-yellow-400 font-medium">{orderMessage}</p>
        )}

        {cartItems.length === 0 ? (
          <div className="text-center mt-20">
            <p className="text-xl text-gray-400">üñºÔ∏è Your cart is empty.</p>
            <p className="text-sm text-gray-500 mt-2">Add some beautiful art to your collection!</p>
          </div>
        ) : (
          <div className="space-y-6">
            {cartItems.map((item) => (
              <div
                key={item._id}
                className="bg-white/10 border border-cyan-300/20 p-4 rounded-xl shadow-md hover:shadow-cyan-500/30 backdrop-blur-md transition-all duration-300 flex items-center justify-between"
              >
                <div>
                  <h2 className="text-lg font-semibold text-cyan-200">{item.title}</h2>
                  <p className="text-sm text-gray-300">üí∞ ‚Çπ{item.price}</p>
                </div>
                <button
                  onClick={() => removeFromCart(item._id)}
                  className="text-red-400 hover:text-red-600 transition-all duration-200"
                >
                  ‚ùå Remove
                </button>
              </div>
            ))}

            <div className="text-center mt-6">
              <button
                onClick={handlePlaceOrder}
                className="bg-gradient-to-r from-blue-500 to-cyan-400 hover:from-cyan-400 hover:to-blue-600 text-white font-bold px-8 py-3 rounded-full shadow-lg hover:shadow-cyan-500/50 transition-transform duration-300 hover:scale-105"
              >
                üßæ Place Order
              </button>
            </div>
          </div>
        )}
      </div>

      <style>{`
        .drop-shadow-glow {
          text-shadow: 0 0 8px rgba(0, 255, 255, 0.6);
        }
      `}</style>
    </div>
  );
}
