import { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

export default function Home() {
  const [arts, setArts] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/artworks').then(res => setArts(res.data));
  }, []);

  return (
    <div>
      <h1>All Artworks</h1>
      {arts.map(art => (
        <div key={art._id}>
          <Link to={`/art/${art._id}`}>
            <img src={`http://localhost:5000${art.image}`} width={200} />
            <h3>{art.title}</h3>
            <p>â‚¹{art.price}</p>
          </Link>
        </div>
      ))}
    </div>
  );
}
